  app.post("/api/auth/login-email", async (req, res) => {
    try {
      const { email, password } = z.object({
        email: z.string().email(),
        password: z.string().min(1)
      }).parse(req.body);

      console.log(`ðŸ”¥ EMAIL LOGIN - Attempting login for: ${email}`);

      // Find user by email
      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(404).json({ message: "Account not found. Please check your email or create a new account." });
      }

      if (!user.isActive) {
        return res.status(403).json({ message: "Account is not active" });
      }

      // Check if user has a password set (for accounts created via email/OTP vs phone/OTP)
      if (!user.password) {
        return res.status(400).json({ 
          message: "This account was created with phone verification. Please use the 'Forgot Password' feature to set up email login, or contact support for assistance.",
          needsPasswordSetup: true
        });
      }

      // Verify password
      const passwordValid = await bcrypt.compare(password, user.password);
      if (!passwordValid) {
        return res.status(401).json({ message: "Incorrect password. Please check your password and try again." });
      }

      // Generate JWT token
      const token = jwt.sign(
        { 
          userId: user.id, 
          role: user.role,
          ...(user.clinicId && { clinicId: user.clinicId })
        },
        process.env.SESSION_SECRET || 'your-super-secret-jwt-key-change-this-in-production',
        { expiresIn: '7d' }
      );

      // Create auth session
      const expiresAt = new Date();
      expiresAt.setDate(expiresAt.getDate() + 7);

      await storage.createAuthSession({
        token,
        userId: user.id,
        ipAddress: req.ip || 'unknown',
        userAgent: req.get('User-Agent') || 'unknown',
        expiresAt,
        lastActivity: new Date()
      });

      console.log(`ðŸ”¥ EMAIL LOGIN - Login successful for: ${user.email}`);

      res.json({
        token,
        user: {
          id: user.id,
          role: user.role,
          firstName: user.firstName,
          lastName: user.lastName,
          email: user.email,
          isActive: user.isActive
        }
      });
