  async getQueueTokensByClinic(clinicId: string): Promise<(QueueToken & { patient: User; doctor: User })[]> {
    const tokens = await db
      .select()
      .from(queueTokens)
      .where(eq(queueTokens.clinicId, clinicId))
      .orderBy(asc(queueTokens.tokenNumber));

    // Get patient and doctor details separately
    const enrichedTokens = [];
    for (const token of tokens) {
      const patient = await this.getUser(token.patientId);
      const doctor = await this.getUser(token.doctorId);

      if (patient && doctor) {
        enrichedTokens.push({
          ...token,
          patient,
          doctor
        });
      }
    }

    return enrichedTokens as (QueueToken & { patient: User; doctor: User })[];
  }

