  .mobile-flex-between {
    @apply flex justify-between items-center;
  }

  .mobile-flex-center {
    @apply flex justify-center items-center;
  }

  /* Mobile spacing utilities */
  .mobile-px-4 {
    @apply px-4;
  }

  .mobile-py-3 {
    @apply py-3;
  }

  .mobile-py-4 {
    @apply py-4;
  }

  .mobile-gap-3 {
    @apply gap-3;
  }

  .mobile-gap-4 {
    @apply gap-4;
  }

  .mobile-mb-4 {
    @apply mb-4;
  }

  .mobile-mb-6 {
    @apply mb-6;
  }

  .mobile-mt-4 {
