  /* Patient records button container fix */
  .patient-record-card {
    @apply overflow-hidden;
  }

  .patient-record-card .flex.gap-2:last-child {
    @apply flex-wrap justify-end min-w-0;
    max-width: 100%;
  }

  .patient-record-card .flex.gap-2 > button {
    @apply text-xs px-2 py-1;
    min-width: 65px;
    white-space: nowrap;
  }

  /* Universal card button container fix */
  .mobile-card-buttons {
    @apply flex flex-wrap gap-1 justify-end min-w-0;
    max-width: 200px;
  }

  .mobile-card-buttons button {
    @apply text-xs px-2 py-1;
    min-width: 60px;
    max-width: 90px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  /* Specific button sizing for different actions */
  .mobile-card-buttons button[data-testid*="reschedule"] {
    min-width: 80px;
  }

  .mobile-card-buttons button[data-testid*="view-history"] {
    min-width: 85px;
