  }

  /* Responsive button layout for very small screens */
  @media (max-width: 480px) {
    .mobile-card-buttons {
      max-width: 150px;
    }

    .mobile-card-buttons button {
      @apply text-xs px-1 py-1;
      min-width: 50px;
      max-width: 70px;
      font-size: 10px;
    }
  }

  /* Container overflow protection */
  .card-with-buttons {
    @apply overflow-hidden;
  }

  .card-with-buttons .flex.justify-between {
    @apply min-w-0;
  }

  .card-with-buttons .flex.justify-between > div:last-child {
    @apply min-w-0 flex-shrink-0;
    max-width: 50%;
  }
}

