import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  ArrowLeft, Plus, Pill, Clock, Upload, Calendar, 
  Trash2, Edit, CheckCircle, XCircle, AlertCircle
} from "lucide-react";

interface CustomMedicine {
  id?: string;
  name: string;
  dosage: string;
  frequency: string;
  timings?: string[];
  instructions: string;
  startDate: string;
  endDate?: string;
  status: 'active' | 'completed' | 'paused';
  medicine?: any;
}

interface MedicineReminder {
  id: string;
  scheduledAt: string;
  isTaken: boolean;
  isSkipped: boolean;
  notes: string;
}

export default function MedicinesPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isUploadDialogOpen, setIsUploadDialogOpen] = useState(false);
  const [uploadText, setUploadText] = useState("");
  const [editingMedicine, setEditingMedicine] = useState<CustomMedicine | null>(null);
  const [newMedicine, setNewMedicine] = useState<CustomMedicine>({
    name: "",
    dosage: "",
    frequency: "once_daily",
    timings: ["08:00"] as string[],
    instructions: "",
    startDate: new Date().toISOString().split('T')[0],
    endDate: "",
    status: "active"
  });
  const [medicineSearchQuery, setMedicineSearchQuery] = useState("");

  // Fetch custom medicines
  const { data: customMedicines = [], isLoading: loadingCustom } = useQuery<CustomMedicine[]>({
    queryKey: ['/api/custom-medicines'],
  });

  // Fetch reminders  
  const { data: reminders = [], isLoading: loadingReminders } = useQuery<MedicineReminder[]>({
    queryKey: ['/api/reminders'],
  });

  // Fetch missed doses
  const { data: missedDoses = [] } = useQuery<any[]>({
    queryKey: ['/api/reminders/missed'],
  });

  // Add custom medicine mutation
  const addMedicineMutation = useMutation({
    mutationFn: async (medicine: CustomMedicine) => {
      const medicineData = {
        ...medicine,
        clinicId: 'default-clinic-id' // Add required clinicId
      };
      const response = await apiRequest('POST', '/api/custom-medicines', medicineData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/custom-medicines'] });
      queryClient.invalidateQueries({ queryKey: ['/api/reminders'] });
      setIsAddDialogOpen(false);
      resetNewMedicine();
      toast({ title: "Medicine Added", description: "Your custom medicine has been added successfully." });
    }
  });

  // Edit medicine mutation
  const editMedicineMutation = useMutation({
    mutationFn: async (medicine: CustomMedicine) => {
      const medicineData = {
        ...medicine,
        clinicId: 'default-clinic-id' // Add required clinicId
      };
      const response = await apiRequest('PUT', `/api/custom-medicines/${medicine.id}`, medicineData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/custom-medicines'] });
      queryClient.invalidateQueries({ queryKey: ['/api/reminders'] });
      setIsEditDialogOpen(false);
      setEditingMedicine(null);
      toast({ title: "Medicine Updated", description: "Your medicine has been updated successfully." });
    }
  });

  // Delete medicine mutation
  const deleteMedicineMutation = useMutation({
    mutationFn: async (medicineId: string) => {
      const response = await apiRequest('DELETE', `/api/custom-medicines/${medicineId}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/custom-medicines'] });
      queryClient.invalidateQueries({ queryKey: ['/api/reminders'] });
      toast({ title: "Medicine Deleted", description: "The medicine has been deleted successfully." });
    }
  });

  // Upload medicines mutation
  const uploadMedicinesMutation = useMutation({
    mutationFn: async (text: string) => {
      const response = await apiRequest('POST', '/api/medicines/upload', { medicineList: text });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/custom-medicines'] });
      queryClient.invalidateQueries({ queryKey: ['/api/reminders'] });
      setIsUploadDialogOpen(false);
      setUploadText("");
      toast({ title: "Medicines Uploaded", description: "Your medicine list has been processed successfully." });
    }
  });

  // Update reminder status
  const updateReminderMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: 'taken' | 'skipped' }) => {
      const response = await apiRequest('PUT', `/api/reminders/${id}`, { status });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/reminders'] });
      toast({ title: "Reminder Updated", description: "Medicine reminder status updated." });
    }
  });

  const addTiming = () => {
    setNewMedicine(prev => ({
      ...prev,
      timings: [...(prev.timings || []), "12:00"]
    }));
  };

  const removeTiming = (index: number) => {
    setNewMedicine(prev => ({
      ...prev,
      timings: (prev.timings || []).filter((_, i) => i !== index)
    }));
  };

  const updateTiming = (index: number, time: string) => {
    setNewMedicine(prev => ({
      ...prev,
      timings: (prev.timings || []).map((t, i) => i === index ? time : t)
    }));
  };

  const resetNewMedicine = () => {
    setNewMedicine({
      name: "", dosage: "", frequency: "once_daily", timings: ["08:00"],
      instructions: "", startDate: new Date().toISOString().split('T')[0],
      endDate: "", status: "active"
    });
  };

  const startEditMedicine = (medicine: CustomMedicine) => {
    setEditingMedicine({ ...medicine });
    setIsEditDialogOpen(true);
  };

  const updateEditingMedicine = (field: keyof CustomMedicine, value: any) => {
    setEditingMedicine(prev => prev ? { ...prev, [field]: value } : null);
  };

  const addEditTiming = () => {
    setEditingMedicine(prev => prev ? {
      ...prev,
      timings: [...(prev.timings || []), "12:00"]
    } : null);
  };

  const removeEditTiming = (index: number) => {
    setEditingMedicine(prev => prev ? {
      ...prev,
      timings: (prev.timings || []).filter((_, i) => i !== index)
    } : null);
  };

  const updateEditTiming = (index: number, time: string) => {
    setEditingMedicine(prev => prev ? {
      ...prev,
      timings: (prev.timings || []).map((t, i) => i === index ? time : t)
    } : null);
  };

  const handleDeleteMedicine = (medicineId: string, medicineName: string) => {
    if (window.confirm(`Are you sure you want to delete "${medicineName}"? This will also delete all associated reminders.`)) {
      deleteMedicineMutation.mutate(medicineId);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-500';
      case 'completed': return 'bg-blue-500';
      case 'paused': return 'bg-yellow-500';
      default: return 'bg-gray-500';
    }
  };

  const formatTime = (time: string) => {
    if (!time) return 'N/A';

    // Handle time string (HH:MM format)
    if (time.includes(':') && !time.includes('T') && !time.includes('Z')) {
      // Ensure proper time format
      const timeFormatted = time.length === 5 ? `${time}:00` : time;
      const date = new Date(`2000-01-01T${timeFormatted}`);
      if (isNaN(date.getTime())) return time; // Return original if invalid
      return date.toLocaleTimeString('en-IN', { 
        hour: '2-digit', 
        minute: '2-digit',
        timeZone: 'Asia/Kolkata'
      });
    }

    // Handle full datetime string - convert from UTC to IST
    const date = new Date(time);
    if (isNaN(date.getTime())) return time; // Return original if invalid

    // Format in Indian Standard Time
    return date.toLocaleTimeString('en-IN', { 
      hour: '2-digit', 
      minute: '2-digit',
      timeZone: 'Asia/Kolkata'
    });
  };

  if (loadingCustom || loadingReminders) {
    return <div className="flex items-center justify-center min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-white">Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 px-4 sm:px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Button variant="ghost" onClick={() => setLocation("/dashboard")}>
              <ArrowLeft className="w-4 h-4" />
            </Button>
            <div className="flex items-center gap-2">
              <Pill className="w-6 h-6 text-blue-500" />
              <h1 className="text-xl font-semibold text-gray-900 dark:text-white">Medicine Manager</h1>
            </div>
          </div>

          <div className="flex gap-2">
            <Dialog open={isUploadDialogOpen} onOpenChange={setIsUploadDialogOpen}>
              <DialogTrigger asChild>
                <Button variant="outline" className="flex items-center gap-2">
                  <Upload className="w-4 h-4" />
                  Upload List
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Upload Medicine List</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <Label>Paste your medicine list</Label>
                    <Textarea
                      placeholder="Example:
Aspirin 75mg - Once daily at 8:00 AM - Take with food
Metformin 500mg - Twice daily at 7:00 AM, 7:00 PM - Before meals
Lisinopril 10mg - Once daily at 9:00 PM - For blood pressure"
                      value={uploadText}
                      onChange={(e) => setUploadText(e.target.value)}
                      rows={8}
                    />
                  </div>
                  <Button 
                    onClick={() => uploadMedicinesMutation.mutate(uploadText)}
                    disabled={uploadMedicinesMutation.isPending || !uploadText.trim()}
                    className="w-full"
                  >
                    {uploadMedicinesMutation.isPending ? "Processing..." : "Upload Medicines"}
                  </Button>
                </div>
              </DialogContent>
            </Dialog>

            <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
              <DialogTrigger asChild>
                <Button className="flex items-center gap-2">
                  <Plus className="w-4 h-4" />
                  Add Medicine
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>Add Custom Medicine</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label>Medicine Name</Label>
                      <Input
                        value={newMedicine.name}
                        onChange={(e) => setNewMedicine(prev => ({ ...prev, name: e.target.value }))}
                        placeholder="e.g., Aspirin"
                      />
                    </div>
                    <div>
                      <Label>Dosage</Label>
                      <Input
                        value={newMedicine.dosage}
                        onChange={(e) => setNewMedicine(prev => ({ ...prev, dosage: e.target.value }))}
                        placeholder="e.g., 75mg, 1 tablet"
                      />
                    </div>
                  </div>

                  <div>
                    <Label>Frequency</Label>
                    <Select value={newMedicine.frequency} onValueChange={(value) => 
                      setNewMedicine(prev => ({ ...prev, frequency: value }))}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="once_daily">Once Daily</SelectItem>
                        <SelectItem value="twice_daily">Twice Daily</SelectItem>
                        <SelectItem value="three_times_daily">Three Times Daily</SelectItem>
                        <SelectItem value="four_times_daily">Four Times Daily</SelectItem>
                        <SelectItem value="as_needed">As Needed</SelectItem>
                        <SelectItem value="weekly">Weekly</SelectItem>
                        <SelectItem value="monthly">Monthly</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <Label>Timings</Label>
                    <div className="space-y-2">
                      {(newMedicine.timings || []).map((time, index) => (
                        <div key={index} className="flex items-center gap-2">
                          <Input
                            type="time"
                            value={time}
                            onChange={(e) => updateTiming(index, e.target.value)}
                            className="flex-1"
                          />
                          {(newMedicine.timings || []).length > 1 && (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => removeTiming(index)}
                            >
                              <Trash2 className="w-4 h-4" />
                            </Button>
                          )}
                        </div>
                      ))}
                      <Button variant="outline" onClick={addTiming} className="w-full">
                        <Plus className="w-4 h-4 mr-2" />
                        Add Timing
                      </Button>
                    </div>
                  </div>

                  <div>
                    <Label>Instructions</Label>
                    <Textarea
                      value={newMedicine.instructions}
                      onChange={(e) => setNewMedicine(prev => ({ ...prev, instructions: e.target.value }))}
                      placeholder="e.g., Take with food, before meals, etc."
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label>Start Date</Label>
                      <Input
                        type="date"
                        value={newMedicine.startDate}
                        onChange={(e) => setNewMedicine(prev => ({ ...prev, startDate: e.target.value }))}
                      />
                    </div>
                    <div>
                      <Label>End Date (Optional)</Label>
                      <Input
                        type="date"
                        value={newMedicine.endDate}
                        onChange={(e) => setNewMedicine(prev => ({ ...prev, endDate: e.target.value }))}
                      />
                    </div>
                  </div>

                  <Button 
                    onClick={() => addMedicineMutation.mutate(newMedicine)}
                    disabled={addMedicineMutation.isPending || !newMedicine.name || !newMedicine.dosage}
                    className="w-full"
                  >
                    {addMedicineMutation.isPending ? "Adding..." : "Add Medicine"}
                  </Button>
                </div>
              </DialogContent>
            </Dialog>

            {/* Edit Medicine Dialog */}
            <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
              <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>Edit Medicine</DialogTitle>
                </DialogHeader>
                {editingMedicine && (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label>Medicine Name</Label>
                        <Input
                          value={editingMedicine.name}
                          onChange={(e) => updateEditingMedicine('name', e.target.value)}
                          placeholder="e.g., Aspirin"
                          data-testid="input-edit-medicine-name"
                        />
                      </div>
                      <div>
                        <Label>Dosage</Label>
                        <Input
                          value={editingMedicine.dosage}
                          onChange={(e) => updateEditingMedicine('dosage', e.target.value)}
                          placeholder="e.g., 75mg, 1 tablet"
                          data-testid="input-edit-medicine-dosage"
                        />
                      </div>
                    </div>

                    <div>
                      <Label>Frequency</Label>
                      <Select value={editingMedicine.frequency} onValueChange={(value) => updateEditingMedicine('frequency', value)}>
                        <SelectTrigger data-testid="select-edit-medicine-frequency">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="once_daily">Once Daily</SelectItem>
                          <SelectItem value="twice_daily">Twice Daily</SelectItem>
                          <SelectItem value="three_times_daily">Three Times Daily</SelectItem>
                          <SelectItem value="four_times_daily">Four Times Daily</SelectItem>
                          <SelectItem value="as_needed">As Needed</SelectItem>
                          <SelectItem value="weekly">Weekly</SelectItem>
                          <SelectItem value="monthly">Monthly</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label>Timings</Label>
                      <div className="space-y-2">
                        {(editingMedicine.timings || []).map((time, index) => (
                          <div key={index} className="flex items-center gap-2">
                            <Input
                              type="time"
                              value={time}
                              onChange={(e) => updateEditTiming(index, e.target.value)}
                              className="flex-1"
                              data-testid={`input-edit-timing-${index}`}
                            />
                            {(editingMedicine.timings || []).length > 1 && (
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => removeEditTiming(index)}
                                data-testid={`button-remove-timing-${index}`}
                              >
                                <Trash2 className="w-4 h-4" />
                              </Button>
                            )}
                          </div>
                        ))}
                        <Button variant="outline" onClick={addEditTiming} className="w-full" data-testid="button-add-edit-timing">
                          <Plus className="w-4 h-4 mr-2" />
                          Add Timing
                        </Button>
                      </div>
                    </div>

                    <div>
                      <Label>Instructions</Label>
                      <Textarea
                        value={editingMedicine.instructions}
                        onChange={(e) => updateEditingMedicine('instructions', e.target.value)}
                        placeholder="e.g., Take with food, before meals, etc."
                        data-testid="textarea-edit-medicine-instructions"
                      />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label>Start Date</Label>
                        <Input
                          type="date"
                          value={editingMedicine.startDate}
                          onChange={(e) => updateEditingMedicine('startDate', e.target.value)}
                          data-testid="input-edit-medicine-start-date"
                        />
                      </div>
                      <div>
                        <Label>End Date (Optional)</Label>
                        <Input
                          type="date"
                          value={editingMedicine.endDate || ''}
                          onChange={(e) => updateEditingMedicine('endDate', e.target.value)}
                          data-testid="input-edit-medicine-end-date"
                        />
                      </div>
                    </div>

                    <div>
                      <Label>Status</Label>
                      <Select value={editingMedicine.status} onValueChange={(value) => updateEditingMedicine('status', value)}>
                        <SelectTrigger data-testid="select-edit-medicine-status">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="active">Active</SelectItem>
                          <SelectItem value="completed">Completed</SelectItem>
                          <SelectItem value="paused">Paused</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div className="flex gap-3">
                      <Button variant="outline" onClick={() => setIsEditDialogOpen(false)} className="flex-1">
                        Cancel
                      </Button>
                      <Button 
                        onClick={() => editMedicineMutation.mutate(editingMedicine)}
                        disabled={editMedicineMutation.isPending || !editingMedicine.name || !editingMedicine.dosage}
                        className="flex-1"
                        data-testid="button-save-edit-medicine"
                      >
                        {editMedicineMutation.isPending ? "Saving..." : "Save Changes"}
                      </Button>
                    </div>
                  </div>
                )}
              </DialogContent>
            </Dialog>
          </div>
        </div>

        {/* Search Bar for Medicines */}
        <div className="px-4 sm:px-6 py-4 bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700">
          <div className="relative">
            <Input
              placeholder="Search medicines by name, dosage, frequency, or instructions..."
              value={medicineSearchQuery}
              onChange={(e) => setMedicineSearchQuery(e.target.value)}
              className="pl-4 pr-4"
              data-testid="input-medicine-search"
            />
          </div>
        </div>
      </header>

      <div className="max-w-6xl mx-auto p-6">
        <Tabs defaultValue="medicines" className="space-y-6">
          <TabsList>
            <TabsTrigger value="medicines">My Medicines</TabsTrigger>
            <TabsTrigger value="reminders">Today's Reminders</TabsTrigger>
          </TabsList>

          <TabsContent value="medicines">
            <div className="grid gap-4">
              {customMedicines.length === 0 ? (
                <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
                  <CardContent className="p-8 text-center">
                    <Pill className="w-12 h-12 text-gray-400 dark:text-gray-500 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">No medicines added yet</h3>
                    <p className="text-gray-500 dark:text-gray-400 mb-4">Add your first medicine or upload a list to get started</p>
                    <div className="flex gap-2 justify-center">
                      <Button onClick={() => setIsAddDialogOpen(true)}>
                        <Plus className="w-4 h-4 mr-2" />
                        Add Medicine
                      </Button>
                      <Button variant="outline" onClick={() => setIsUploadDialogOpen(true)}>
                        <Upload className="w-4 h-4 mr-2" />
                        Upload List
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ) : (
                customMedicines
                  .filter((medicine: CustomMedicine) => {
                    if (!medicineSearchQuery) return true;
                    const query = medicineSearchQuery.toLowerCase();
                    const name = (medicine.name || '').toLowerCase();
                    const dosage = (medicine.dosage || '').toLowerCase();
                    const frequency = (medicine.frequency || '').toLowerCase();
                    const instructions = (medicine.instructions || '').toLowerCase();

                    return name.includes(query) ||
                           dosage.includes(query) ||
                           frequency.includes(query) ||
                           instructions.includes(query);
                  })
                  .map((medicine: CustomMedicine) => {
                  const medicineStats = missedDoses.find((md: any) => md.medicineName === medicine.name);
                  const hasOverdueOrMissed = medicineStats && (medicineStats.missedDoses > 0 || medicineStats.overdueToday > 0);
                  console.log('ðŸ”¥ MEDICINE DEBUG:', { 
                    id: medicine.id, 
                    name: medicine.name, 
                    dosage: medicine.dosage, 
                    hasData: !!medicine.name 
                  });
                  return (
                    <Card key={medicine.id} className={`medicine-card border-gray-200 dark:border-gray-700 ${hasOverdueOrMissed ? 'overdue glow-urgent' : ''}`}>
                      <CardHeader>
                        <div className="flex items-center justify-between">
                          <CardTitle className="flex items-center gap-2 font-bold text-lg">
                            <Pill className="w-5 h-5 text-blue-500" />
                            <span className="font-semibold">
                              {medicine.name || 'MISSING MEDICINE NAME'}
                            </span>
                          </CardTitle>
                          <div className="flex items-center gap-2">
                            <Badge className={getStatusColor(medicine.status)}>
                              {medicine.status}
                            </Badge>
                            <div className="flex gap-1">
                              <Button
                                size="sm"
                                variant="ghost"
                                onClick={() => startEditMedicine(medicine)}
                                disabled={editMedicineMutation.isPending}
                                data-testid={`button-edit-medicine-${medicine.id}`}
                              >
                                <Edit className="w-4 h-4" />
                              </Button>
                              <Button
                                size="sm"
                                variant="ghost"
                                onClick={() => handleDeleteMedicine(medicine.id!, medicine.name)}
                                disabled={deleteMedicineMutation.isPending}
                                className="text-red-600 hover:text-red-700 hover:bg-red-50 dark:text-red-400 dark:hover:text-red-300 dark:hover:bg-red-900/20"
                                data-testid={`button-delete-medicine-${medicine.id}`}
                              >
                                <Trash2 className="w-4 h-4" />
                              </Button>
                            </div>
                          </div>
                        </div>
                        {medicineStats && (medicineStats.missedDoses > 0 || medicineStats.overdueToday > 0) && (
                          <div className="flex gap-2 mt-2">
                            {medicineStats.missedDoses > 0 && (
                              <Badge className="bg-red-100 text-red-800 text-xs pulse-urgent">
                                {medicineStats.missedDoses} missed doses
                              </Badge>
                            )}
                            {medicineStats.overdueToday > 0 && (
                              <Badge className="bg-orange-100 text-orange-800 text-xs pulse-delay">
                                {medicineStats.overdueToday} overdue today
                              </Badge>
                            )}
                          </div>
                        )}
                      </CardHeader>
                      <CardContent>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div>
                            <p className="text-base font-medium">
                              Dosage: <span className="font-semibold">{medicine.dosage || 'No dosage'}</span>
                            </p>
                            <p className="text-base font-medium">
                              Frequency: <span className="font-semibold">{medicine.frequency?.replace('_', ' ') || 'Not specified'}</span>
                            </p>
                            <p className="text-base font-medium">
                              Instructions: <span className="font-semibold">{medicine.instructions || 'Take as prescribed'}</span>
                            </p>
                          </div>
                        <div>
                          <p className="text-base mb-2 font-medium">Timings:</p>
                          <div className="flex flex-wrap gap-2">
                            {(medicine.timings || []).map((time, index) => (
                              <Badge key={index} variant="outline" className="flex items-center gap-1">
                                <Clock className="w-3 h-3" />
                                <span>{formatTime(time)}</span>
                              </Badge>
                            ))}
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                  );
                })
              )}
            </div>
          </TabsContent>

          <TabsContent value="reminders">
            <div className="grid gap-4">
              {reminders.length === 0 ? (
                <Card className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
                  <CardContent className="p-8 text-center">
                    <Clock className="w-12 h-12 text-gray-400 dark:text-gray-500 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">No reminders for today</h3>
                    <p className="text-gray-500 dark:text-gray-400">Add some medicines to see reminders here</p>
                  </CardContent>
                </Card>
              ) : (
                reminders.map((reminder: any) => (
                  <Card key={reminder.id} className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700">
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                          <div className={`w-3 h-3 rounded-full ${
                            reminder.isTaken ? 'bg-green-500' : 
                            reminder.isSkipped ? 'bg-red-500' : 
                            new Date(reminder.scheduledAt) < new Date() ? 'bg-orange-500' : 'bg-yellow-500'
                          }`} />
                          <div>
                            <h4 className="font-medium text-gray-900 dark:text-white">{reminder.medicineName || reminder.prescription?.medicine?.name || 'Medicine'}</h4>
                            <p className="text-sm text-gray-600 dark:text-gray-300">
                              {reminder.dosage || reminder.prescription?.dosage} - {formatTime(reminder.scheduledAt)}
                              {!reminder.isTaken && !reminder.isSkipped && new Date(reminder.scheduledAt) < new Date() && (
                                <Badge className="bg-orange-100 text-orange-800 text-xs ml-2">Overdue</Badge>
                              )}
                            </p>
                            {reminder.notes && (
                              <p className="text-sm text-gray-500">{reminder.notes}</p>
                            )}
                          </div>
                        </div>

                        {!reminder.isTaken && !reminder.isSkipped && (
                          <div className="flex gap-2">
                            <Button
                              size="sm"
                              onClick={() => updateReminderMutation.mutate({ id: reminder.id, status: 'taken' })}
                              disabled={updateReminderMutation.isPending}
                            >
                              <CheckCircle className="w-4 h-4 mr-1" />
                              Taken
                            </Button>
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => updateReminderMutation.mutate({ id: reminder.id, status: 'skipped' })}
                              disabled={updateReminderMutation.isPending}
                            >
                              <XCircle className="w-4 h-4 mr-1" />
                              Skip
                            </Button>
                          </div>
                        )}

                        {(reminder.isTaken || reminder.isSkipped) && (
                          <div className="flex gap-2 items-center">
                            <Badge className={reminder.isTaken ? "bg-green-500" : "bg-gray-500"}>
                              {reminder.isTaken ? (
                                <>
                                  <CheckCircle className="w-3 h-3 mr-1" />
                                  Taken
                                </>
                              ) : (
                                <>
                                  <XCircle className="w-3 h-3 mr-1" />
                                  Skipped
                                </>
                              )}
                            </Badge>
                            <Button
                              size="sm"
                              variant="outline"
                              className="bg-orange-500 hover:bg-orange-600 text-white border-orange-500"
                              onClick={() => updateReminderMutation.mutate({ id: reminder.id, status: 'skipped' })}
                              disabled={updateReminderMutation.isPending}
                            >
                              <AlertCircle className="w-3 h-3 mr-1" />
                              Correct
                            </Button>
                          </div>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                ))
              )}
            </div>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { User, Phone, Mail, Calendar, MapPin, Heart, ArrowLeft, Stethoscope } from "lucide-react";

export default function ProfilePage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    dateOfBirth: "",
    address: "",
    emergencyContact: "",
  });

  // Check authentication and redirect if needed
  useEffect(() => {
    const token = localStorage.getItem("auth_token");
    if (!token) {
      setLocation("/patient-login");
      return;
    }
  }, [setLocation]);

  // Fetch user data from API
  const { data: profileData, isLoading } = useQuery({
    queryKey: ["/api/users/me"],
    enabled: !!localStorage.getItem("auth_token"),
  });

  // Update form data when profile data is loaded
  useEffect(() => {
    if (profileData) {
      setFormData({
        firstName: profileData.firstName || "",
        lastName: profileData.lastName || "",
        email: profileData.email || "",
        dateOfBirth: profileData.dateOfBirth ? profileData.dateOfBirth.split('T')[0] : "",
        address: profileData.address || "",
        emergencyContact: profileData.emergencyContact || "",
      });
    }
  }, [profileData]);

  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("PUT", "/api/users/me", data);
      return response.json();
    },
    onSuccess: (updatedUser) => {
      toast({
        title: "Profile Updated",
        description: "Your profile has been successfully updated.",
      });
      // Update localStorage user data if it exists
      const existingUser = localStorage.getItem("user");
      if (existingUser) {
        localStorage.setItem("user", JSON.stringify(updatedUser));
      }
      queryClient.invalidateQueries({ queryKey: ["/api/users/me"] });
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update profile",
        variant: "destructive",
      });
    },
  });

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSaveProfile = () => {
    updateProfileMutation.mutate(formData);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <div className="flex items-center gap-4">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setLocation("/dashboard")}
              className="flex items-center gap-2"
            >
              <ArrowLeft className="w-4 h-4" />
              Back to Dashboard
            </Button>
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Profile Settings</h1>
              <p className="text-sm text-gray-600">Manage your personal information</p>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-4xl mx-auto px-4 py-8">
        <div className="grid gap-6">
          {/* Profile Information Card */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="w-5 h-5 text-blue-500" />
                Personal Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="firstName">First Name</Label>
                  <Input
                    id="firstName"
                    value={formData.firstName}
                    onChange={(e) => handleInputChange("firstName", e.target.value)}
                    placeholder="Enter your first name"
                    data-testid="input-firstName"
                  />
                </div>
                <div>
                  <Label htmlFor="lastName">Last Name</Label>
                  <Input
                    id="lastName"
                    value={formData.lastName}
                    onChange={(e) => handleInputChange("lastName", e.target.value)}
                    placeholder="Enter your last name"
                    data-testid="input-lastName"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="email">Email Address</Label>
                <div className="relative">
                  <Mail className="absolute left-3 top-3 w-4 h-4 text-gray-400" />
                  <Input
                    id="email"
                    type="email"
                    value={formData.email}
                    onChange={(e) => handleInputChange("email", e.target.value)}
                    placeholder="Enter your email address"
                    className="pl-10"
                    data-testid="input-email"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="dateOfBirth">Date of Birth</Label>
                <div className="relative">
                  <Calendar className="absolute left-3 top-3 w-4 h-4 text-gray-400" />
                  <Input
                    id="dateOfBirth"
                    type="date"
                    value={formData.dateOfBirth}
                    onChange={(e) => handleInputChange("dateOfBirth", e.target.value)}
                    className="pl-10"
                    data-testid="input-dateOfBirth"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="address">Address</Label>
                <div className="relative">
                  <MapPin className="absolute left-3 top-3 w-4 h-4 text-gray-400" />
                  <Textarea
                    id="address"
                    value={formData.address}
                    onChange={(e) => handleInputChange("address", e.target.value)}
                    placeholder="Enter your complete address"
                    className="pl-10 resize-none"
                    rows={3}
                    data-testid="input-address"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="emergencyContact">Emergency Contact</Label>
                <div className="relative">
                  <Heart className="absolute left-3 top-3 w-4 h-4 text-gray-400" />
                  <Input
                    id="emergencyContact"
                    value={formData.emergencyContact}
                    onChange={(e) => handleInputChange("emergencyContact", e.target.value)}
                    placeholder="Emergency contact phone number"
                    className="pl-10"
                    data-testid="input-emergencyContact"
                  />
                </div>
              </div>

              <div className="pt-4">
                <Button
                  onClick={handleSaveProfile}
                  disabled={updateProfileMutation.isPending}
                  className="bg-blue-500 hover:bg-blue-600"
                  data-testid="button-save-profile"
                >
                  {updateProfileMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Medical History Card */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Stethoscope className="w-5 h-5 text-blue-500" />
                Medical History
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="allergies">Known Allergies</Label>
                <Input
                  id="allergies"
                  value={formData.allergies || ""}
                  onChange={(e) => handleInputChange("allergies", e.target.value)}
                  placeholder="List any allergies (e.g., penicillin, peanuts)"
                />
              </div>
              <div>
                <Label htmlFor="chronicConditions">Chronic Conditions</Label>
                <Input
                  id="chronicConditions"
                  value={formData.chronicConditions || ""}
                  onChange={(e) => handleInputChange("chronicConditions", e.target.value)}
                  placeholder="List chronic conditions (e.g., diabetes, hypertension)"
                />
              </div>
              <div>
                <Label htmlFor="bloodType">Blood Type</Label>
                <Input
                  id="bloodType"
                  value={formData.bloodType || ""}
                  onChange={(e) => handleInputChange("bloodType", e.target.value)}
                  placeholder="Enter blood type (e.g., A+, B-, O+)"
                />
              </div>
              <div>
                <Label htmlFor="emergencyContact">Emergency Contact</Label>
                <Input
                  id="emergencyContact"
                  value={formData.emergencyContact || ""}
                  onChange={(e) => handleInputChange("emergencyContact", e.target.value)}
                  placeholder="Emergency contact name and phone"
                />
              </div>
              <div className="pt-4">
                <Button
                  onClick={handleSaveProfile}
                  disabled={updateProfileMutation.isPending}
                  className="bg-blue-500 hover:bg-blue-600 w-full"
                >
                  {updateProfileMutation.isPending ? "Saving..." : "Save Medical Information"}
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

client/src/components/CancelModal.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { AlertTriangle, Calendar, X } from "lucide-react";

interface CancelModalProps {
  isOpen: boolean;
  onClose: () => void;
  appointment?: any;
}

export default function CancelModal({ isOpen, onClose, appointment }: CancelModalProps) {
  const { toast } = useToast();
  const [reason, setReason] = useState("");

  // Cancel appointment mutation
  const cancelMutation = useMutation({
    mutationFn: (data: { appointmentId: string; reason: string }) => 
      apiRequest('PUT', `/api/appointments/${data.appointmentId}/cancel`, { reason: data.reason }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/appointments'] });
      onClose();
      setReason("");
      toast({
        title: "Appointment Cancelled",
        description: "Your appointment has been cancelled successfully. You'll receive a confirmation SMS.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Cancellation Failed",
        description: error.message || "Failed to cancel appointment. Please try again.",
        variant: "destructive",
      });
    }
  });

  const handleCancel = () => {
    if (!appointment?.id) {
      toast({
        title: "No Appointment",
        description: "No appointment selected for cancellation.",
        variant: "destructive",
      });
      return;
    }

    cancelMutation.mutate({
      appointmentId: appointment.id,
      reason: reason.trim() || "No reason provided"
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-red-600">
            <AlertTriangle className="w-5 h-5" />
            Cancel Appointment
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Appointment Details */}
          {appointment && (
            <div className="bg-gray-50 p-4 rounded-lg">
              <h4 className="font-medium mb-2">Appointment Details</h4>
              <div className="space-y-1 text-sm">
                <p><strong>Doctor:</strong> {appointment.doctor?.firstName} {appointment.doctor?.lastName}</p>
                <p><strong>Date:</strong> {new Date(appointment.appointmentDate).toLocaleDateString()}</p>
                <p><strong>Time:</strong> {new Date(appointment.appointmentDate).toLocaleTimeString()}</p>
                <p><strong>Type:</strong> {appointment.type?.replace('_', ' ')}</p>
              </div>
            </div>
          )}

          {/* Cancellation Reason */}
          <div>
            <Label htmlFor="reason">Reason for Cancellation (Optional)</Label>
            <Textarea
              id="reason"
              placeholder="Please let us know why you're cancelling..."
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              className="mt-2"
              rows={3}
            />
          </div>

          {/* Warning */}
          <div className="bg-red-50 border border-red-200 rounded-lg p-3">
            <div className="flex items-start gap-2">
              <AlertTriangle className="w-4 h-4 text-red-500 mt-0.5 flex-shrink-0" />
              <div className="text-sm text-red-700">
                <p className="font-medium">Cancellation Policy</p>
                <p className="mt-1">Cancelling within 24 hours may incur a cancellation fee. Emergency cancellations are exempt.</p>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex gap-3 pt-2">
            <Button
              variant="outline"
              onClick={onClose}
              disabled={cancelMutation.isPending}
              className="flex-1"
            >
              Keep Appointment
            </Button>
            <Button
              onClick={handleCancel}
              disabled={cancelMutation.isPending}
              className="flex-1 bg-red-600 hover:bg-red-700 text-white"
            >
              {cancelMutation.isPending ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Cancelling...
                </>
              ) : (
                <>
                  <X className="w-4 h-4 mr-2" />
                  Cancel Appointment
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

