import React, { useState, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Calendar, Clock, User, MapPin, Video, Home, Stethoscope, Phone, CheckCircle, LogOut } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { queryClient, apiRequest } from '@/lib/queryClient';

interface Doctor {
  id: string;
  firstName: string;
  lastName: string;
  phoneNumber: string;
  specialization?: string;
  isActive: boolean;
}

export default function PatientBooking() {
  const [step, setStep] = useState(1);
  const [bookingData, setBookingData] = useState({
    symptoms: '',
    appointmentType: '',
    doctorId: '',
    preferredDate: '',
    preferredTime: '',
    urgency: 'normal',
    notes: ''
  });
  const [isSubmitted, setIsSubmitted] = useState(false);
  const { toast } = useToast();

  // Check authentication and redirect if not logged in
  useEffect(() => {
    const token = localStorage.getItem('auth_token');
    if (!token) {
      window.location.href = '/login';
      return;
    }
  }, []);

  // Get current patient info
  const { data: currentPatient, isLoading: patientLoading } = useQuery<any>({
    queryKey: ['/api/users/me'],
    retry: (failureCount, error) => {
      if (error && typeof error === 'object' && 'status' in error && error.status === 401) {
        localStorage.removeItem('auth_token');
        window.location.href = '/login';
        return false;
      }
      return failureCount < 2;
    }
  });

  // Redirect non-patients
  useEffect(() => {
    if (currentPatient && currentPatient.role !== 'patient') {
      if (currentPatient.role === 'admin') {
        window.location.href = '/admin-dashboard';
      } else {
        window.location.href = '/dashboard';
      }
    }
  }, [currentPatient]);

  // Fetch available doctors
  const { data: doctors = [], isLoading: doctorsLoading } = useQuery<Doctor[]>({
    queryKey: ['/api/doctors'],
    enabled: step >= 2
  });

  // Submit appointment request mutation
  const submitAppointment = useMutation({
    mutationFn: async (data: typeof bookingData) => {
      return apiRequest('/api/appointments/patient-request', {
        method: 'POST',
        body: JSON.stringify({
          doctorId: data.doctorId,
          type: data.appointmentType,
          symptoms: data.symptoms,
          preferredDate: `${data.preferredDate}T${data.preferredTime}:00.000Z`,
          urgency: data.urgency,
          notes: data.notes
        })
      });
    },
    onSuccess: () => {
      setIsSubmitted(true);
      queryClient.invalidateQueries({ queryKey: ['/api/appointments'] });
      toast({
        title: "Appointment Request Submitted!",
        description: "Your appointment request has been sent to the clinic for approval. You'll receive an SMS notification once it's reviewed.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/appointments'] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit appointment request. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleLogout = () => {
    localStorage.removeItem('auth_token');
    window.location.href = '/login';
  };

  if (patientLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!currentPatient) {
    return null; // Will redirect in useEffect
  }

  const handleNext = () => {
    if (step === 1) {
      // Validate symptoms
      if (!bookingData.symptoms) {
        toast({
          title: "Required Fields Missing",
          description: "Please describe your symptoms.",
          variant: "destructive"
        });
        return;
      }
    }
    setStep(step + 1);
  };

  const handleSubmit = () => {
    if (!bookingData.doctorId || !bookingData.appointmentType || !bookingData.preferredDate || !bookingData.preferredTime) {
      toast({
        title: "Required Fields Missing",
        description: "Please complete all appointment details.",
        variant: "destructive"
      });
      return;
    }
    submitAppointment.mutate(bookingData);
  };

  if (isSubmitted) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardContent className="text-center py-8">
            <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-green-700 mb-2">Request Submitted!</h2>
            <p className="text-gray-600 mb-4">
              Your appointment request has been sent to the clinic administration for review.
            </p>
            <div className="bg-blue-50 p-4 rounded-lg mb-4">
              <p className="text-sm text-blue-700">
                <strong>What happens next?</strong><br />
                â€¢ Admin will review your request<br />
                â€¢ You'll get SMS notification when approved<br />
                â€¢ Appointment will be confirmed with date/time
              </p>
            </div>
            <Button onClick={() => {
              setIsSubmitted(false);
              setStep(1);
              setBookingData({
                patientFirstName: '',
                patientLastName: '',
                patientPhone: '',
                patientEmail: '',
                dateOfBirth: '',
                symptoms: '',
                appointmentType: '',
                doctorId: '',
                preferredDate: '',
                preferredTime: '',
                urgency: 'normal',
                notes: ''
              });
            }} className="w-full">
              Book Another Appointment
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-2xl mx-auto">
        <div className="mb-8 text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Book an Appointment</h1>
          <p className="text-gray-600">Complete the form below to request an appointment with our healthcare professionals</p>
        </div>

        {/* Progress Steps */}
        <div className="flex items-center justify-center mb-8">
          <div className="flex items-center">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
              step >= 1 ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-500'
            }`}>
              1
            </div>
            <div className="text-xs ml-2 text-gray-600">Patient Info</div>
          </div>
          <div className={`w-16 h-1 mx-4 ${step >= 2 ? 'bg-blue-600' : 'bg-gray-200'}`}></div>
          <div className="flex items-center">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
              step >= 2 ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-500'
            }`}>
              2
            </div>
            <div className="text-xs ml-2 text-gray-600">Appointment</div>
          </div>
          <div className={`w-16 h-1 mx-4 ${step >= 3 ? 'bg-blue-600' : 'bg-gray-200'}`}></div>
          <div className="flex items-center">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
              step >= 3 ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-500'
            }`}>
              3
            </div>
            <div className="text-xs ml-2 text-gray-600">Review</div>
          </div>
        </div>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              {step === 1 && <><User className="w-5 h-5" /> Patient Information</>}
              {step === 2 && <><Calendar className="w-5 h-5" /> Appointment Details</>}
              {step === 3 && <><CheckCircle className="w-5 h-5" /> Review & Submit</>}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Step 1: Patient Information */}
            {step === 1 && (
              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="firstName">First Name *</Label>
                    <Input
                      id="firstName"
                      value={bookingData.patientFirstName}
                      onChange={(e) => setBookingData({...bookingData, patientFirstName: e.target.value})}
                      placeholder="Enter your first name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="lastName">Last Name *</Label>
                    <Input
                      id="lastName"
                      value={bookingData.patientLastName}
                      onChange={(e) => setBookingData({...bookingData, patientLastName: e.target.value})}
                      placeholder="Enter your last name"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="phone">Phone Number *</Label>
                  <Input
                    id="phone"
                    value={bookingData.patientPhone}
                    onChange={(e) => setBookingData({...bookingData, patientPhone: e.target.value})}
                    placeholder="+1234567890"
                  />
                </div>
                <div>
                  <Label htmlFor="email">Email (Optional)</Label>
                  <Input
                    id="email"
                    type="email"
                    value={bookingData.patientEmail}
                    onChange={(e) => setBookingData({...bookingData, patientEmail: e.target.value})}
                    placeholder="your.email@example.com"
                  />
                </div>
                <div>
                  <Label htmlFor="dob">Date of Birth (Optional)</Label>
                  <Input
                    id="dob"
                    type="date"
                    value={bookingData.dateOfBirth}
                    onChange={(e) => setBookingData({...bookingData, dateOfBirth: e.target.value})}
                  />
                </div>
                <div>
                  <Label htmlFor="symptoms">Describe Your Symptoms</Label>
                  <Textarea
                    id="symptoms"
                    value={bookingData.symptoms}
                    onChange={(e) => setBookingData({...bookingData, symptoms: e.target.value})}
                    placeholder="Please describe your symptoms or reason for the appointment..."
                    rows={3}
                  />
                </div>
              </div>
            )}

            {/* Step 2: Appointment Details */}
            {step === 2 && (
              <div className="space-y-4">
                <div>
                  <Label>Appointment Type *</Label>
                  <div className="grid grid-cols-3 gap-4 mt-2">
                    <div 
                      onClick={() => setBookingData({...bookingData, appointmentType: 'clinic'})}
                      className={`p-4 border rounded-lg cursor-pointer text-center ${
                        bookingData.appointmentType === 'clinic' ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                      }`}
                    >
                      <Stethoscope className="w-8 h-8 mx-auto mb-2 text-blue-600" />
                      <div className="font-medium">In-Clinic</div>
                      <div className="text-xs text-gray-500">Visit our clinic</div>
                    </div>
                    <div 
                      onClick={() => setBookingData({...bookingData, appointmentType: 'home'})}
                      className={`p-4 border rounded-lg cursor-pointer text-center ${
                        bookingData.appointmentType === 'home' ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                      }`}
                    >
                      <Home className="w-8 h-8 mx-auto mb-2 text-green-600" />
                      <div className="font-medium">Home Visit</div>
                      <div className="text-xs text-gray-500">Doctor visits you</div>
                    </div>
                    <div 
                      onClick={() => setBookingData({...bookingData, appointmentType: 'video'})}
                      className={`p-4 border rounded-lg cursor-pointer text-center ${
                        bookingData.appointmentType === 'video' ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                      }`}
                    >
                      <Video className="w-8 h-8 mx-auto mb-2 text-purple-600" />
                      <div className="font-medium">Video Call</div>
                      <div className="text-xs text-gray-500">Online consultation</div>
                    </div>
                  </div>
                </div>

                <div>
                  <Label>Select Doctor *</Label>
                  {doctorsLoading ? (
                    <div className="text-center py-4">Loading doctors...</div>
                  ) : (
                    <div className="grid gap-3 mt-2">
                      {doctors.map((doctor) => (
                        <div
                          key={doctor.id}
                          onClick={() => setBookingData({...bookingData, doctorId: doctor.id})}
                          className={`p-4 border rounded-lg cursor-pointer ${
                            bookingData.doctorId === doctor.id ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                          }`}
                        >
                          <div className="flex items-center justify-between">
                            <div>
                              <div className="font-medium">{doctor.firstName?.startsWith('Dr. ') ? `${doctor.firstName} ${doctor.lastName}` : `Dr. ${doctor.firstName} ${doctor.lastName}`}</div>
                              <div className="text-sm text-gray-500">{doctor.phoneNumber}</div>
                              {doctor.specialization && (
                                <Badge variant="outline" className="mt-1">{doctor.specialization}</Badge>
                              )}
                            </div>
                            {doctor.isActive && (
                              <Badge className="bg-green-100 text-green-800">Available</Badge>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="date">Preferred Date *</Label>
                    <Input
                      id="date"
                      type="date"
                      value={bookingData.preferredDate}
                      onChange={(e) => setBookingData({...bookingData, preferredDate: e.target.value})}
                      min={new Date().toISOString().split('T')[0]}
                    />
                  </div>
                  <div>
                    <Label htmlFor="time">Preferred Time *</Label>
                    <Input
                      id="time"
                      type="time"
                      value={bookingData.preferredTime}
                      onChange={(e) => setBookingData({...bookingData, preferredTime: e.target.value})}
                    />
                  </div>
                </div>

                <div>
                  <Label>Urgency Level</Label>
                  <Select value={bookingData.urgency} onValueChange={(value) => setBookingData({...bookingData, urgency: value})}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="low">Low - Routine checkup</SelectItem>
                      <SelectItem value="normal">Normal - Standard appointment</SelectItem>
                      <SelectItem value="high">High - Need attention soon</SelectItem>
                      <SelectItem value="urgent">Urgent - Emergency situation</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="notes">Additional Notes (Optional)</Label>
                  <Textarea
                    id="notes"
                    value={bookingData.notes}
                    onChange={(e) => setBookingData({...bookingData, notes: e.target.value})}
                    placeholder="Any additional information for the doctor..."
                    rows={2}
                  />
                </div>
              </div>
            )}

            {/* Step 3: Review */}
            {step === 3 && (
              <div className="space-y-4">
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h3 className="font-medium mb-3">Patient Information</h3>
                  <div className="text-sm space-y-1">
                    <div><strong>Name:</strong> {bookingData.patientFirstName} {bookingData.patientLastName}</div>
                    <div><strong>Phone:</strong> {bookingData.patientPhone}</div>
                    {bookingData.patientEmail && <div><strong>Email:</strong> {bookingData.patientEmail}</div>}
                    {bookingData.dateOfBirth && <div><strong>Date of Birth:</strong> {bookingData.dateOfBirth}</div>}
                  </div>
                </div>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <h3 className="font-medium mb-3">Appointment Details</h3>
                  <div className="text-sm space-y-1">
                    <div><strong>Type:</strong> {bookingData.appointmentType}</div>
                    <div><strong>Doctor:</strong> Dr. {doctors.find(d => d.id === bookingData.doctorId)?.firstName} {doctors.find(d => d.id === bookingData.doctorId)?.lastName}</div>
                    <div><strong>Preferred Date:</strong> {bookingData.preferredDate}</div>
                    <div><strong>Preferred Time:</strong> {bookingData.preferredTime}</div>
                    <div><strong>Urgency:</strong> {bookingData.urgency}</div>
                  </div>
                </div>

                {bookingData.symptoms && (
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <h3 className="font-medium mb-2">Symptoms</h3>
                    <p className="text-sm">{bookingData.symptoms}</p>
                  </div>
                )}

                {bookingData.notes && (
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <h3 className="font-medium mb-2">Additional Notes</h3>
                    <p className="text-sm">{bookingData.notes}</p>
                  </div>
                )}

                <div className="bg-blue-50 p-4 rounded-lg">
                  <p className="text-sm text-blue-700">
                    <strong>Important:</strong> This is a request for an appointment. The clinic administration will review your request and contact you to confirm the appointment details.
                  </p>
                </div>
              </div>
            )}

            {/* Navigation Buttons */}
            <div className="flex gap-4 pt-4">
              {step > 1 && (
                <Button variant="outline" onClick={() => setStep(step - 1)} className="flex-1">
                  Previous
                </Button>
              )}
              {step < 3 ? (
                <Button onClick={handleNext} className="flex-1">
                  Next
                </Button>
              ) : (
                <Button 
                  onClick={handleSubmit} 
                  className="flex-1"
                  disabled={submitAppointment.isPending}
                >
                  {submitAppointment.isPending ? 'Submitting...' : 'Submit Request'}
                </Button>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/patient-login.tsx.content:
import React, { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { signInWithGoogle, signInWithEmail } from '@/lib/firebase';
import { Mail, Lock, Chrome, LogIn } from 'lucide-react';

export default function PatientLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const loginWithFirebaseMutation = useMutation({
    mutationFn: async (firebaseUser: any) => {
      const response = await apiRequest('POST', '/api/auth/firebase-login', {
        firebaseUid: firebaseUser.uid,
        email: firebaseUser.email,
        name: firebaseUser.displayName || firebaseUser.email?.split('@')[0] || 'Patient'
      });
      return await response.json();
    },
    onSuccess: (data: any) => {
      if (data.token && data.user) {
        localStorage.setItem('auth_token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        toast({
          title: "Login Successful!",
          description: "Welcome back to SmartClinic.",
        });

        // Redirect based on user role
        if (data.user.role === 'admin') {
          window.location.href = '/admin-dashboard';
        } else if (data.user.role === 'staff' || data.user.role === 'doctor') {
          window.location.href = '/dashboard';
        } else {
          window.location.href = '/dashboard';
        }
      }
    },
    onError: (error: any) => {
      console.error("Firebase login mutation failed:", error);
      setIsLoading(false);

      let errorMessage = "Authentication failed. Please try again.";

      // Check if it's a response error with detailed message
      if (error.response?.data?.emailNotFound) {
        errorMessage = "No account found with this email. Please create an account first, then try logging in with Google.";
      } else if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      } else if (error.message) {
        errorMessage = error.message;
      }

      toast({
        title: "Google Login Failed",
        description: errorMessage,
        variant: "destructive"
      });
    }
  });

  const handleGoogleLogin = async () => {
    setIsLoading(true);
    try {
      const user = await signInWithGoogle();
      await loginWithFirebaseMutation.mutateAsync(user);
    } catch (error: any) {
      let errorMessage = "Failed to login with Google. Please try again.";

      if (error.message?.includes('domain')) {
        errorMessage = "Google sign-in is not configured for this domain. Please use email login instead.";
      } else if (error.message?.includes('popup')) {
        errorMessage = "Popup was blocked. Please allow popups or use email login.";
      } else if (error.message) {
        errorMessage = error.message;
      }

      toast({
        title: "Google Login Failed",
        description: errorMessage,
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleEmailLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email || !password) {
      toast({
        title: "Missing Information",
        description: "Please enter both email and password.",
        variant: "destructive"
      });
      return;
    }

    setIsLoading(true);
    try {
      // First try Firebase authentication
      const user = await signInWithEmail(email, password);
      await loginWithFirebaseMutation.mutateAsync(user);
    } catch (firebaseError: any) {
      console.log("Firebase login failed, trying database authentication...");

      // If Firebase fails, try direct database authentication
      try {
        const response = await apiRequest('POST', '/api/auth/login-email', {
          email,
          password
        });
        const data = await response.json();

        if (data.token && data.user) {
          localStorage.setItem('auth_token', data.token);
          localStorage.setItem('user', JSON.stringify(data.user));
          toast({
            title: "Login Successful!",
            description: "Welcome back to SmartClinic.",
          });

          // Redirect based on user role
          if (data.user.role === 'admin') {
            window.location.href = '/admin-dashboard';
          } else {
            window.location.href = '/dashboard';
          }
        }
      } catch (dbError: any) {
        let errorResponse = {};
        try {
          if (dbError.response) {
            errorResponse = await dbError.response.json();
          }
        } catch (e) {
          // If parsing fails, use the error message directly
        }

        if (errorResponse.needsPasswordSetup) {
          toast({
            title: "Password Setup Required",
            description: "Your account was created with phone verification. Please set up a password first.",
            variant: "destructive"
          });

          // Show password setup form
          const newPassword = prompt("Set a new password for email login (minimum 6 characters):");
          if (newPassword && newPassword.length >= 6) {
            try {
              const response = await apiRequest('POST', '/api/auth/set-password', {
                email,
                newPassword
              });

              toast({
                title: "Password Set Successfully!",
                description: "You can now login with your email and password.",
              });

              // Retry login with new password
              setTimeout(() => {
                setPassword(newPassword);
              }, 1000);

            } catch (setPasswordError: any) {
              toast({
                title: "Failed to Set Password",
                description: "Please try again or contact support.",
                variant: "destructive"
              });
            }
          }
        } else {
          toast({
            title: "Login Failed",
            description: firebaseError.message || "Invalid email or password. Please try again.",
            variant: "destructive"
          });
        }
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        <Card className="shadow-xl border-0 bg-white/95 dark:bg-gray-900/95 backdrop-blur-sm">
          <CardHeader className="text-center pb-4">
            <div className="mx-auto bg-blue-100 dark:bg-blue-900 w-16 h-16 rounded-full flex items-center justify-center mb-4">
              <LogIn className="w-8 h-8 text-blue-600 dark:text-blue-400" />
            </div>
            <CardTitle className="text-2xl font-bold text-gray-900 dark:text-white">Patient Login</CardTitle>
            <p className="text-gray-600 dark:text-gray-400 mt-2">Access your healthcare dashboard</p>
          </CardHeader>

          <CardContent className="space-y-4">
            {/* Google Login Button */}
            <Button
              type="button"
              variant="outline"
              className="w-full py-3 border-2 hover:bg-gray-50 dark:hover:bg-gray-800"
              onClick={handleGoogleLogin}
              disabled={isLoading}
              data-testid="button-google-login"
            >
              <Chrome className="w-5 h-5 mr-2" />
              Continue with Google
            </Button>

            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <span className="w-full border-t border-gray-300 dark:border-gray-600" />
              </div>
              <div className="relative flex justify-center text-xs uppercase">
                <span className="bg-white dark:bg-gray-900 px-2 text-gray-500">Or login with email</span>
              </div>
            </div>

            {/* Email Login Form */}
            <form onSubmit={handleEmailLogin} className="space-y-4">
              <div>
                <Label htmlFor="email">Email Address</Label>
                <div className="relative">
                  <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                  <Input
                    id="email"
                    type="email"
                    placeholder="Enter your email address"
                    className="pl-10"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    data-testid="input-email"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="password">Password</Label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                  <Input
                    id="password"
                    type="password"
                    placeholder="Enter your password"
                    className="pl-10"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    data-testid="input-password"
                  />
                </div>
              </div>

              <Button 
                type="submit" 
                className="w-full py-3 bg-blue-600 hover:bg-blue-700 text-white"
                disabled={isLoading || loginWithFirebaseMutation.isPending}
                data-testid="button-login"
              >
                {isLoading || loginWithFirebaseMutation.isPending ? "Signing In..." : "Sign In"}
              </Button>
            </form>

            <div className="text-center pt-4">
              <p className="text-sm text-gray-600 dark:text-gray-400">
                Don't have an account?{' '}
                <a href="/patient-signup" className="text-blue-600 hover:text-blue-500 font-medium">
                  Create account
                </a>
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

server/services/email.ts.content:
// Email service for sending OTP via email using multiple providers
import nodemailer from 'nodemailer';
import { Resend } from 'resend';

class EmailService {
  private transporter: nodemailer.Transporter | null = null;
  private resend: Resend | null = null;

  constructor() {
    this.initializeServices();
  }

  private async initializeServices() {
    try {
      // Try Gmail SMTP first if credentials are available
      if (process.env.GMAIL_USER && process.env.GMAIL_APP_PASSWORD) {
        this.transporter = nodemailer.createTransport({
          service: 'gmail',
          auth: {
            user: process.env.GMAIL_USER,
            pass: process.env.GMAIL_APP_PASSWORD,
          },
        });
        console.log('ðŸ”¥ EMAIL SERVICE - Initialized with Gmail SMTP (real email delivery to any address)');
        return;
      }

      // Try to initialize Resend if API key is available (limited to verified email only)
      if (process.env.RESEND_API_KEY) {
        this.resend = new Resend(process.env.RESEND_API_KEY);
        console.log('ðŸ”¥ EMAIL SERVICE - Initialized with Resend (limited to 44441100sf@gmail.com only)');
        return;
      }

      // Fallback to Ethereal Email for testing
      const testAccount = await nodemailer.createTestAccount();

      this.transporter = nodemailer.createTransport({
        host: 'smtp.ethereal.email',
        port: 587,
        secure: false,
        auth: {
          user: testAccount.user,
          pass: testAccount.pass,
        },
      });

      console.log('ðŸ”¥ EMAIL SERVICE - Initialized with Ethereal test account (preview only)');
    } catch (error) {
      console.error('ðŸ”¥ EMAIL SERVICE - Failed to initialize:', error);
    }
  }

  async sendOtp(email: string, otp: string): Promise<{ success: boolean; otp?: string; error?: string; previewUrl?: string }> {
    const emailContent = {
      subject: 'Your SmartClinic Login Code',
      html: `
        <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
          <div style="text-align: center; margin-bottom: 30px;">
            <h1 style="color: #2563eb; margin: 0;">SmartClinic</h1>
            <p style="color: #666; margin: 5px 0;">Healthcare Management System</p>
          </div>

          <div style="background: #f8fafc; border-radius: 8px; padding: 30px; text-align: center; margin-bottom: 20px;">
            <h2 style="color: #1e293b; margin-bottom: 20px;">Your Login Code</h2>
            <div style="background: white; border: 2px solid #e2e8f0; border-radius: 8px; padding: 20px; margin: 20px 0;">
              <div style="font-size: 32px; font-weight: bold; color: #2563eb; letter-spacing: 8px;">${otp}</div>
            </div>
            <p style="color: #64748b; margin: 15px 0;">Enter this code to complete your login</p>
            <p style="color: #ef4444; font-size: 14px; margin: 10px 0;">This code expires in 5 minutes</p>
          </div>

          <div style="border-top: 1px solid #e2e8f0; padding-top: 20px; text-align: center;">
            <p style="color: #94a3b8; font-size: 12px; margin: 5px 0;">
              If you didn't request this code, please ignore this email.
            </p>
            <p style="color: #94a3b8; font-size: 12px; margin: 5px 0;">
              SmartClinic - Your trusted healthcare partner
            </p>
          </div>
        </div>
      `,
      text: `Your SmartClinic login code is: ${otp}\n\nThis code expires in 5 minutes.\n\nIf you didn't request this code, please ignore this email.\n\nSmartClinic - Your trusted healthcare partner`
    };

    try {
      // Try Gmail SMTP first (works with any email address)
      if (this.transporter && process.env.GMAIL_USER) {
        const mailOptions = {
          from: `"SmartClinic" <${process.env.GMAIL_USER}>`,
          to: email,
          subject: emailContent.subject,
          html: emailContent.html,
          text: emailContent.text
        };

        const info = await this.transporter.sendMail(mailOptions);
        console.log(`ðŸ”¥ EMAIL OTP SERVICE - Gmail SMTP response:`, info);
        console.log(`ðŸ”¥ EMAIL OTP SERVICE - Real email sent to ${email} via Gmail SMTP`);
        console.log(`ðŸ”¥ EMAIL OTP CODE for ${email}: ${otp}`);
        return { success: true };
      }

      // Try Resend (limited to verified email only)
      if (this.resend && email === '44441100sf@gmail.com') {
        const { data, error } = await this.resend.emails.send({
          from: 'SmartClinic <onboarding@resend.dev>',
          to: [email],
          subject: emailContent.subject,
          html: emailContent.html,
          text: emailContent.text,
        });

        if (error) {
          console.error('ðŸ”¥ RESEND ERROR:', error);
          throw new Error('Resend failed');
        }

        console.log(`ðŸ”¥ EMAIL OTP SERVICE - Real email sent to ${email} via Resend`);
        return { success: true };
      }

      // Fallback to Ethereal Email (testing only)
      if (!this.transporter) {
        await this.initializeServices();
      }

      if (!this.transporter) {
        throw new Error('No email service available');
      }

      const mailOptions = {
        from: '"SmartClinic" <noreply@smartclinic.com>',
        to: email,
        subject: emailContent.subject,
        html: emailContent.html,
        text: emailContent.text
      };

      const info = await this.transporter.sendMail(mailOptions);
      const previewUrl = nodemailer.getTestMessageUrl(info);

      console.log(`ðŸ”¥ EMAIL OTP SERVICE - Test email sent to ${email}`);
      console.log(`ðŸ”¥ EMAIL PREVIEW URL: ${previewUrl}`);

      return { 
        success: true, 
        otp,
        previewUrl: previewUrl || undefined
      };
    } catch (error) {
      console.error('ðŸ”¥ EMAIL SERVICE ERROR:', error);

      // Final fallback: console logging for development
      console.log(`ðŸ”¥ EMAIL FALLBACK - OTP for ${email}: ${otp}`);
      return { 
        success: true, 
        otp,
        error: 'Email service unavailable, check console for OTP'
      };
    }
  }

  async sendAppointmentApproved(
    email: string,
    details: { doctorName: string; appointmentDate: string; appointmentTime: string; clinic?: string }
  ): Promise<{ success: boolean; error?: string }> {
    const emailContent = {
      subject: 'âœ… Appointment Confirmed - SmartClinic',
      html: `
        <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
          <div style="text-align: center; margin-bottom: 30px;">
            <h1 style="color: #16a34a; margin: 0;">âœ… Appointment Confirmed</h1>
            <p style="color: #666; margin: 5px 0;">SmartClinic Healthcare Management</p>
          </div>

          <div style="background: #f0fdf4; border-radius: 8px; padding: 30px; margin-bottom: 20px;">
            <h2 style="color: #166534; margin-bottom: 20px;">Your appointment has been confirmed!</h2>
            <div style="color: #15803d; line-height: 1.6;">
              <p><strong>Doctor:</strong> ${details.doctorName}</p>
              <p><strong>Date:</strong> ${details.appointmentDate}</p>
              <p><strong>Time:</strong> ${details.appointmentTime}</p>
              ${details.clinic ? `<p><strong>Location:</strong> ${details.clinic}</p>` : ''}
            </div>
          </div>

          <div style="background: #fef3c7; border-radius: 8px; padding: 20px; margin-bottom: 20px;">
            <p style="color: #92400e; margin: 0; font-weight: bold;">ðŸ“‹ Please bring:</p>
            <ul style="color: #92400e; margin: 10px 0;">
              <li>Valid ID or insurance card</li>
              <li>List of current medications</li>
              <li>Any relevant medical records</li>
            </ul>
          </div>

          <div style="border-top: 1px solid #e2e8f0; padding-top: 20px; text-align: center;">
            <p style="color: #94a3b8; font-size: 12px;">SmartClinic - Your trusted healthcare partner</p>
          </div>
        </div>
      `,
      text: `Appointment Confirmed!\n\nYour appointment has been confirmed:\nDoctor: ${details.doctorName}\nDate: ${details.appointmentDate}\nTime: ${details.appointmentTime}\n\nPlease bring valid ID and current medications list.\n\nSmartClinic - Your trusted healthcare partner`
    };

    return await this.sendNotificationEmail(email, emailContent);
  }

  async sendAppointmentRejected(
    email: string,
    details: { doctorName: string; reason: string; appointmentId: string }
  ): Promise<{ success: boolean; error?: string }> {
    const emailContent = {
      subject: 'âŒ Appointment Request Update - SmartClinic',
      html: `
        <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
          <div style="text-align: center; margin-bottom: 30px;">
            <h1 style="color: #dc2626; margin: 0;">âŒ Appointment Request Update</h1>
            <p style="color: #666; margin: 5px 0;">SmartClinic Healthcare Management</p>
          </div>

          <div style="background: #fef2f2; border-radius: 8px; padding: 30px; margin-bottom: 20px;">
            <h2 style="color: #991b1b; margin-bottom: 20px;">Appointment Request Status</h2>
            <div style="color: #dc2626; line-height: 1.6;">
              <p>We regret to inform you that your appointment request with <strong>${details.doctorName}</strong> could not be confirmed at this time.</p>
              <p><strong>Reason:</strong> ${details.reason}</p>
            </div>
          </div>

          <div style="background: #f0f9ff; border-radius: 8px; padding: 20px; margin-bottom: 20px;">
            <p style="color: #0369a1; margin: 0; font-weight: bold;">ðŸ’¡ Next Steps:</p>
