  private async sendNotificationEmail(
    email: string,
    emailContent: { subject: string; html: string; text: string }
  ): Promise<{ success: boolean; error?: string }> {
    try {
      // Try Gmail SMTP first (works with any email address)
      if (this.transporter && process.env.GMAIL_USER) {
        const mailOptions = {
          from: `"SmartClinic" <${process.env.GMAIL_USER}>`,
          to: email,
          subject: emailContent.subject,
          html: emailContent.html,
          text: emailContent.text
        };

        const info = await this.transporter.sendMail(mailOptions);
        console.log(`ðŸ”¥ EMAIL NOTIFICATION - Real email sent to ${email} via Gmail SMTP`);
        return { success: true };
      }

      // Try Resend (limited to verified email only)
      if (this.resend && email === '44441100sf@gmail.com') {
        const { data, error } = await this.resend.emails.send({
          from: 'SmartClinic <onboarding@resend.dev>',
          to: [email],
          subject: emailContent.subject,
          html: emailContent.html,
          text: emailContent.text,
        });

        if (error) {
          console.error('ðŸ”¥ RESEND ERROR:', error);
          throw new Error('Resend failed');
        }

        console.log(`ðŸ”¥ EMAIL NOTIFICATION - Real email sent to ${email} via Resend`);
        return { success: true };
      }

      // Fallback to Ethereal Email (testing only)
      if (!this.transporter) {
        await this.initializeServices();
      }

      if (!this.transporter) {
        throw new Error('No email service available');
      }

      const mailOptions = {
        from: '"SmartClinic" <noreply@smartclinic.com>',
        to: email,
        subject: emailContent.subject,
        html: emailContent.html,
        text: emailContent.text
      };

      const info = await this.transporter.sendMail(mailOptions);
      const previewUrl = nodemailer.getTestMessageUrl(info);

      console.log(`ðŸ”¥ EMAIL NOTIFICATION - Test email sent to ${email}`);
      console.log(`ðŸ”¥ EMAIL PREVIEW URL: ${previewUrl}`);

      return { success: true };
    } catch (error) {
      console.error('ðŸ”¥ EMAIL NOTIFICATION ERROR:', error);

      // Final fallback: console logging for development
      console.log(`ðŸ”¥ EMAIL FALLBACK - Notification for ${email}: ${emailContent.subject}`);
      return { 
        success: true,
        error: 'Email service unavailable, check console for notification'
      };
    }
  }

