import React, { useState, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Calendar, Clock, User, MapPin, Video, Home, Stethoscope, Phone, CheckCircle, LogOut } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { queryClient, apiRequest } from '@/lib/queryClient';

interface Doctor {
  id: string;
  firstName: string;
  lastName: string;
  phoneNumber: string;
  specialization?: string;
  isActive: boolean;
}

export default function PatientBooking() {
  const [step, setStep] = useState(1);
  const [bookingData, setBookingData] = useState({
    symptoms: '',
    appointmentType: '',
    doctorId: '',
    preferredDate: '',
    preferredTime: '',
    urgency: 'normal',
    notes: ''
  });
  const [isSubmitted, setIsSubmitted] = useState(false);
  const { toast } = useToast();

  // Check authentication and redirect if not logged in
  useEffect(() => {
    const token = localStorage.getItem('auth_token');
    if (!token) {
      window.location.href = '/login';
      return;
    }
  }, []);

  // Get current patient info
  const { data: currentPatient, isLoading: patientLoading } = useQuery<any>({
    queryKey: ['/api/users/me'],
    retry: (failureCount, error) => {
      if (error && typeof error === 'object' && 'status' in error && error.status === 401) {
        localStorage.removeItem('auth_token');
        window.location.href = '/login';
        return false;
      }
      return failureCount < 2;
    }
  });

  // Redirect non-patients
  useEffect(() => {
    if (currentPatient && currentPatient.role !== 'patient') {
      if (currentPatient.role === 'admin') {
        window.location.href = '/admin-dashboard';
      } else {
        window.location.href = '/dashboard';
      }
    }
  }, [currentPatient]);

  // Fetch available doctors
  const { data: doctors = [], isLoading: doctorsLoading } = useQuery<Doctor[]>({
    queryKey: ['/api/doctors'],
    enabled: step >= 2
  });

  // Submit appointment request mutation
  const submitAppointment = useMutation({
    mutationFn: async (data: typeof bookingData) => {
      return apiRequest('POST', '/api/appointments/patient-request', {
        doctorId: data.doctorId,
        type: data.appointmentType,
        symptoms: data.symptoms,
        preferredDate: `${data.preferredDate}T${data.preferredTime}:00.000Z`,
        urgency: data.urgency,
        notes: data.notes
      });
    },
    onSuccess: () => {
      setIsSubmitted(true);
      queryClient.invalidateQueries({ queryKey: ['/api/appointments'] });
      toast({
        title: "Appointment Request Submitted!",
        description: "Your appointment request has been submitted for admin approval. You'll receive an SMS notification once reviewed.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Booking Failed",
        description: error.message || "Failed to submit appointment request. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleLogout = () => {
    localStorage.removeItem('auth_token');
    window.location.href = '/login';
  };

  if (patientLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!currentPatient) {
    return null; // Will redirect in useEffect
  }

  const handleNext = () => {
    if (step === 1) {
      // Validate symptoms
      if (!bookingData.symptoms) {
        toast({
          title: "Required Fields Missing",
          description: "Please describe your symptoms.",
          variant: "destructive"
        });
        return;
      }
    }
    setStep(step + 1);
  };

  const handleSubmit = () => {
    if (!bookingData.doctorId || !bookingData.appointmentType || !bookingData.preferredDate || !bookingData.preferredTime) {
      toast({
        title: "Required Fields Missing",
        description: "Please complete all appointment details.",
        variant: "destructive"
      });
      return;
    }
    submitAppointment.mutate(bookingData);
  };

  if (isSubmitted) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardContent className="text-center py-8">
            <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold text-green-700 mb-2">Request Submitted!</h2>
            <p className="text-gray-600 mb-4">
              Your appointment request has been sent to the clinic administration for review.
            </p>
            <div className="bg-blue-50 p-4 rounded-lg mb-4">
              <p className="text-sm text-blue-700">
                <strong>What happens next?</strong><br />
                â€¢ Admin will review your request<br />
                â€¢ You'll get SMS notification when approved<br />
                â€¢ Appointment will be confirmed with date/time
              </p>
            </div>
            <Button onClick={() => {
              setIsSubmitted(false);
              setStep(1);
              setBookingData({
                symptoms: '',
                appointmentType: '',
                doctorId: '',
                preferredDate: '',
                preferredTime: '',
                urgency: 'normal',
                notes: ''
              });
            }} className="w-full">
              Book Another Appointment
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header with Patient Info & Logout */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 mb-2">Book Your Appointment</h1>
            <p className="text-gray-600">Welcome, {currentPatient.firstName} {currentPatient.lastName}</p>
          </div>
          <Button variant="outline" onClick={handleLogout} className="flex items-center gap-2">
            <LogOut className="w-4 h-4" />
            Logout
          </Button>
        </div>

        {/* Progress Steps */}
        <div className="flex justify-center mb-8">
          <div className="flex items-center space-x-4">
            {[1, 2, 3].map((stepNum) => (
              <div key={stepNum} className="flex items-center">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                  step >= stepNum 
                    ? 'bg-blue-600 text-white' 
                    : 'bg-gray-200 text-gray-600'
                }`}>
                  {stepNum}
                </div>
                {stepNum < 3 && (
                  <div className={`w-16 h-1 mx-2 ${
                    step > stepNum ? 'bg-blue-600' : 'bg-gray-200'
                  }`} />
                )}
              </div>
            ))}
          </div>
        </div>

        <Card className="max-w-2xl mx-auto">
          <CardHeader>
            <CardTitle>
              {step === 1 && "Tell us your symptoms"}
              {step === 2 && "Choose your doctor"}
              {step === 3 && "Select appointment details"}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {step === 1 && (
              <div className="space-y-4">
                <div>
                  <Label htmlFor="symptoms">Describe your symptoms *</Label>
                  <Textarea
                    id="symptoms"
                    placeholder="Please describe what symptoms you're experiencing..."
                    value={bookingData.symptoms}
                    onChange={(e) => setBookingData({...bookingData, symptoms: e.target.value})}
                    rows={4}
                  />
                </div>
                <div>
                  <Label htmlFor="urgency">Urgency Level</Label>
                  <Select value={bookingData.urgency} onValueChange={(value) => setBookingData({...bookingData, urgency: value})}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="low">Low - Routine check-up</SelectItem>
                      <SelectItem value="normal">Normal - General concern</SelectItem>
                      <SelectItem value="high">High - Urgent care needed</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <Button onClick={handleNext} className="w-full">
                  Continue to Doctor Selection
                </Button>
              </div>
            )}

            {step === 2 && (
              <div className="space-y-4">
                <div>
                  <Label>Select Your Doctor</Label>
                  {doctorsLoading ? (
                    <div className="text-center py-4">Loading doctors...</div>
                  ) : (
                    <div className="grid gap-3 mt-2">
                      {doctors.map((doctor) => (
                        <div
                          key={doctor.id}
                          className={`p-4 border rounded-lg cursor-pointer transition-all ${
                            bookingData.doctorId === doctor.id
                              ? 'border-blue-500 bg-blue-50'
                              : 'border-gray-200 hover:border-gray-300'
                          }`}
                          onClick={() => setBookingData({...bookingData, doctorId: doctor.id})}
                        >
                          <div className="flex items-center gap-3">
                            <Stethoscope className="w-5 h-5 text-blue-500" />
                            <div>
                              <p className="font-medium">{doctor.firstName?.startsWith('Dr. ') ? `${doctor.firstName} ${doctor.lastName}` : `Dr. ${doctor.firstName} ${doctor.lastName}`}</p>
                              <p className="text-sm text-gray-600">{doctor.specialization || 'General Practitioner'}</p>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
                <div className="flex gap-3">
                  <Button variant="outline" onClick={() => setStep(1)} className="flex-1">
                    Back
                  </Button>
                  <Button onClick={handleNext} disabled={!bookingData.doctorId} className="flex-1">
                    Continue to Details
                  </Button>
                </div>
              </div>
            )}

            {step === 3 && (
              <div className="space-y-4">
                <div>
                  <Label>Appointment Type</Label>
                  <div className="grid grid-cols-3 gap-3 mt-2">
                    {[
                      { value: 'clinic', label: 'Clinic Visit', icon: MapPin },
                      { value: 'home_visit', label: 'Home Visit', icon: Home },
                      { value: 'telehealth', label: 'Video Call', icon: Video }
                    ].map(({ value, label, icon: Icon }) => (
                      <div
                        key={value}
                        className={`p-4 border rounded-lg cursor-pointer text-center transition-all ${
                          bookingData.appointmentType === value
                            ? 'border-blue-500 bg-blue-50'
                            : 'border-gray-200 hover:border-gray-300'
                        }`}
                        onClick={() => setBookingData({...bookingData, appointmentType: value})}
                      >
                        <Icon className="w-6 h-6 mx-auto mb-2 text-blue-500" />
                        <p className="text-sm font-medium">{label}</p>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="preferredDate">Preferred Date</Label>
                    <Input
                      id="preferredDate"
                      type="date"
                      value={bookingData.preferredDate}
                      onChange={(e) => setBookingData({...bookingData, preferredDate: e.target.value})}
                    />
                  </div>
                  <div>
                    <Label htmlFor="preferredTime">Preferred Time</Label>
                    <Input
                      id="preferredTime"
                      type="time"
                      value={bookingData.preferredTime}
                      onChange={(e) => setBookingData({...bookingData, preferredTime: e.target.value})}
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="notes">Additional Notes (Optional)</Label>
                  <Textarea
                    id="notes"
                    placeholder="Any additional information or special requests..."
                    value={bookingData.notes}
                    onChange={(e) => setBookingData({...bookingData, notes: e.target.value})}
                    rows={3}
                  />
                </div>

                <div className="flex gap-3">
                  <Button variant="outline" onClick={() => setStep(2)} className="flex-1">
                    Back
                  </Button>
                  <Button 
                    onClick={handleSubmit} 
                    disabled={submitAppointment.isPending}
                    className="flex-1"
                  >
                    {submitAppointment.isPending ? 'Submitting...' : 'Submit Request'}
                  </Button>
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/live-queue.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useQueueSocket } from "@/hooks/useQueueSocket";
import { 
  Activity, 
  Clock, 
  ArrowLeft, 
  Users, 
  AlertCircle,
  RefreshCw
} from "lucide-react";

export default function LiveQueueTracker() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [user, setUser] = useState<any>(null);
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const token = localStorage.getItem("auth_token");
    const userData = localStorage.getItem("user");

    if (!token || !userData) {
      setLocation("/login");
      return;
    }

    setUser(JSON.parse(userData));
  }, [setLocation]);

  // Update current time every second for real-time calculations
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // Calculate dynamic wait time based on creation time and position
  const calculateDynamicWaitTime = (queueToken: any, position: number): number => {
    if (!queueToken?.createdAt) return 0;

    const createdAt = new Date(queueToken.createdAt);
    const elapsedMinutes = Math.floor((currentTime.getTime() - createdAt.getTime()) / (1000 * 60));

    // Average consultation time per patient (15 minutes)
    const averageConsultationTime = 15;

    // Calculate initial wait time based on queue position
    const baseWaitTime = (position - 1) * averageConsultationTime;

    // Subtract elapsed time to get remaining wait time
    const remainingWaitTime = Math.max(0, baseWaitTime - elapsedMinutes);

    return remainingWaitTime;
  };

  // Get patient's queue position
  const { data: currentQueuePosition } = useQuery({
    queryKey: ["/api/queue/position"],
    enabled: !!user && (user.role === "patient" || user.role === "admin"),
    refetchInterval: 3000,
  });

  // Get admin queue data for full queue view
  const { data: adminQueue = [] } = useQuery({
    queryKey: ["/api/queue/admin"], 
    refetchInterval: 3000,
    retry: false,
  });

  // Use WebSocket for real-time queue updates
  const { queuePosition: liveQueuePosition, isConnected: queueConnected } = useQueueSocket(
    user?.id,
    false // Not admin mode for this view
  );

  // Use live queue position if available, otherwise fallback to API data
  const queuePosition = liveQueuePosition || currentQueuePosition || null;

  // Remove duplicates and filter queue data
  const queueArray = Array.isArray(adminQueue) ? 
    adminQueue.filter((token: any, index: number, array: any[]) => 
      array.findIndex(t => t.id === token.id) === index
    ) : [];

  // Find the first patient in queue (lowest token number) as currently serving
  const sortedQueue = queueArray.filter((token: any) => token.status === 'waiting' || token.status === 'called' || token.status === 'in_progress')
    .sort((a: any, b: any) => a.tokenNumber - b.tokenNumber);
  const currentlyServing = sortedQueue[0]; // First patient is being served
  const waitingQueue = sortedQueue.slice(1); // Rest are waiting

  // Real-time countdown state
  const [countdown, setCountdown] = useState(0);

  // Calculate the current user's queue position for dynamic wait time
  const currentUserPosition = queuePosition?.tokenNumber ? 
    waitingQueue.findIndex((token: any) => token.tokenNumber === queuePosition.tokenNumber) + 1 : 0;

  // Use server's calculated wait time as primary source
  const serverWaitTime = queuePosition?.estimatedWaitTime || 0;

  // If we have valid queue position data, use server time, otherwise try to calculate
  const baseWaitTime = serverWaitTime > 0 ? 
    serverWaitTime : 
    (queuePosition?.createdAt && currentUserPosition > 0 ? 
      calculateDynamicWaitTime(queuePosition, currentUserPosition) : 0);

  // Real-time countdown effect
  useEffect(() => {
    if (baseWaitTime > 0) {
      setCountdown(baseWaitTime);

      const interval = setInterval(() => {
        setCountdown(prev => {
          const newValue = Math.max(0, prev - (1/60)); // Decrease by 1 second (1/60 minute)
          return Math.round(newValue * 10) / 10; // Round to 1 decimal place
        });
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [baseWaitTime]);

  const dynamicWaitTime = Math.ceil(countdown); // Round up for display

  const refreshQueue = () => {
    queryClient.invalidateQueries({ queryKey: ["/api/queue/position"] });
    queryClient.invalidateQueries({ queryKey: ["/api/queue/admin"] });
    toast({
      title: "Queue Refreshed",
      description: "Queue information has been updated.",
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 dark:from-gray-900 dark:via-blue-900 dark:to-indigo-900">
      <div className="container mx-auto px-4 py-6 max-w-4xl">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => setLocation("/dashboard")}
              className="flex items-center gap-2"
            >
              <ArrowLeft className="w-4 h-4" />
              Back to Dashboard
            </Button>
            <div>
              <h1 className="text-2xl font-bold text-gray-900 dark:text-white flex items-center gap-2">
                <Activity className="w-6 h-6 text-blue-500" />
                Live Queue Tracker
              </h1>
              <p className="text-sm text-gray-600 dark:text-gray-400">
                Real-time queue status and position tracking
              </p>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {queueConnected && (
              <Badge className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200">
                <div className="w-2 h-2 bg-green-500 rounded-full mr-2 animate-pulse"></div>
                Live Connected
              </Badge>
            )}
            <Button 
              variant="outline" 
              size="sm"
              onClick={refreshQueue}
              className="flex items-center gap-2"
            >
              <RefreshCw className="w-4 h-4" />
              Refresh
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Your Position Card */}
          <Card className="lg:col-span-2">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Users className="w-5 h-5 text-blue-500" />
                Your Queue Position
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-lg p-8 text-center">
                <div className="text-sm mb-2 opacity-90">
                  Queue Position
                  {queueConnected && <span className="ml-2 text-green-300">â— Live</span>}
                </div>
                <div className="text-5xl font-bold mb-2">
                  #{queuePosition?.tokenNumber || 'N/A'}
                </div>
                <div className="text-lg mb-4 opacity-90">
                  Estimated wait: {dynamicWaitTime > 0 ? `${dynamicWaitTime} minutes` : 'Your turn soon'}
                  {dynamicWaitTime > 0 && (
                    <div className="text-sm opacity-75 mt-1">
                      <div className="flex items-center gap-2">
                        <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                        Counting down live â€¢ {countdown.toFixed(1)}min
                      </div>
                    </div>
                  )}
                </div>
                {queuePosition && queuePosition.status === 'called' && (
                  <Badge className="bg-green-600 text-white text-lg px-4 py-2">
                    <AlertCircle className="w-4 h-4 mr-2" />
                    You're being called! Please proceed to the doctor.
                  </Badge>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Now Serving */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Activity className="w-5 h-5 text-green-500" />
                Now Serving
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="bg-green-500 text-white rounded-lg p-6 text-center">
                <div className="text-sm mb-2">Currently with Doctor</div>
                <div className="text-3xl font-bold mb-1">
                  #{currentlyServing?.tokenNumber || '--'}
                </div>
                <div className="text-sm">
                  {currentlyServing?.patient ? 
                    `${currentlyServing.patient.firstName} ${currentlyServing.patient.lastName}` : 
                    'No patient currently'
                  }
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Queue Status */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="w-5 h-5 text-orange-500" />
                Queue Status
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex items-center justify-between p-3 bg-orange-50 dark:bg-orange-900/20 rounded-lg">
                  <div>
                    <div className="font-medium text-orange-800 dark:text-orange-200">Patients Waiting</div>
                    <div className="text-sm text-orange-600 dark:text-orange-300">In queue ahead of you</div>
                  </div>
                  <div className="text-2xl font-bold text-orange-600">
                    {waitingQueue.length}
                  </div>
                </div>

                <div className="flex items-center justify-between p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                  <div>
                    <div className="font-medium text-blue-800 dark:text-blue-200">Average Wait</div>
                    <div className="text-sm text-blue-600 dark:text-blue-300">Per patient</div>
                  </div>
                  <div className="text-2xl font-bold text-blue-600">
                    ~15min
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Upcoming Queue */}
          <Card className="lg:col-span-2">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Users className="w-5 h-5 text-gray-600" />
                Upcoming Queue ({waitingQueue.length} patients waiting)
              </CardTitle>
            </CardHeader>
            <CardContent>
              {waitingQueue.length > 0 ? (
                <div className="space-y-3">
                  {waitingQueue.slice(0, 8).map((token: any, index: number) => {
                    const isCurrentUser = token.patientId === user?.id;
                    const isNext = index === 0;
                    const bgColor = isCurrentUser ? 'bg-blue-100 dark:bg-blue-900/30 border-blue-300' : 
                                   isNext ? 'bg-green-50 dark:bg-green-900/20 border-green-200' : 
                                   'bg-gray-50 dark:bg-gray-800';
                    const borderColor = isCurrentUser ? 'border-l-4 border-l-blue-500' : 
                                       isNext ? 'border-l-4 border-l-green-500' : '';

                    // Calculate dynamic wait time for this token
                    const tokenPosition = index + 1;
                    const tokenDynamicWaitTime = token.estimatedWaitTime || Math.max(0, (tokenPosition - 1) * 15);

                    return (
                      <div key={token.id} className={`flex items-center justify-between p-4 rounded-lg ${bgColor} ${borderColor}`}>
                        <div className="flex items-center gap-3">
                          <Badge className={`${isCurrentUser ? 'bg-blue-500' : isNext ? 'bg-green-500' : 'bg-gray-500'} text-white`}>
                            #{token.tokenNumber}
                          </Badge>
                          <div>
                            <div className="font-medium">
                              {isCurrentUser ? 'You' : `${token.patient?.firstName || 'Patient'} ${token.patient?.lastName || ''}`}
                              {isNext && <span className="ml-2 text-green-600 font-semibold">- Next</span>}
                              {isCurrentUser && <span className="ml-2 text-blue-600 font-semibold">- Your Turn</span>}
                            </div>
                            <div className="text-sm text-gray-500">
                              Est. time: {tokenDynamicWaitTime > 0 ? 
                                new Date(Date.now() + tokenDynamicWaitTime * 60000).toLocaleTimeString('en-IN', {
                                  hour: '2-digit', 
                                  minute: '2-digit',
                                  timeZone: 'Asia/Kolkata'
                                }) : 'Soon'
                              }
                            </div>
                          </div>
                        </div>
                        <div className="text-right">
                          <div className="font-medium">
                            {tokenDynamicWaitTime === 0 ? 'Now' : `~${tokenDynamicWaitTime}min`}
                          </div>
                          <div className="text-sm text-gray-500">wait time</div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              ) : (
                <div className="text-center py-8">
                  <Users className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">No Queue Currently</h3>
                  <p className="text-gray-600 dark:text-gray-400">The queue is empty at the moment</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Footer Actions */}
        <div className="mt-8 flex justify-center">
          <Button 
            onClick={() => setLocation("/dashboard")}
            className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2"
          >
            Return to Dashboard
          </Button>
        </div>
      </div>
    </div>
  );
}

