import { useState, useEffect, useRef } from 'react';
import { useToast } from '@/hooks/use-toast';

interface QueuePosition {
  tokenNumber: number | null;
  position: number | null;
  estimatedWaitTime: number;
  status?: string;
}

interface QueueUpdate {
  type: 'queue_position' | 'admin_queue_update' | 'full_queue_update';
  data: any;
}

export function useQueueSocket(patientId?: string, isAdmin = false) {
  const [queuePosition, setQueuePosition] = useState<QueuePosition | null>(null);
  const [queueTokens, setQueueTokens] = useState<any[]>([]);
  const [isConnected, setIsConnected] = useState(false);
  const wsRef = useRef<WebSocket | null>(null);
  const { toast } = useToast();
  const reconnectTimeoutRef = useRef<NodeJS.Timeout>();

  // Debug logging for hook parameters
  console.log('ðŸ”¥ðŸ”¥ðŸ”¥ useQueueSocket called with:', { patientId, isAdmin });

  const connect = () => {
    try {
      const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
      const wsUrl = `${protocol}//${window.location.host}/ws`;

      console.log('ðŸ”¥ Connecting to queue WebSocket:', wsUrl, 'isAdmin:', isAdmin);
      wsRef.current = new WebSocket(wsUrl);

      wsRef.current.onopen = () => {
        console.log('ðŸ”¥ Queue WebSocket connected, isAdmin:', isAdmin, 'patientId:', patientId);
        setIsConnected(true);

        // Subscribe to updates based on user type
        if (isAdmin) {
          console.log('ðŸ”¥ Client: Sending admin queue subscription (isAdmin=true)');
          wsRef.current?.send(JSON.stringify({ type: 'subscribe_admin_queue' }));
          console.log('ðŸ”¥ Client: Admin subscription message sent successfully');
        } else if (patientId) {
          console.log('ðŸ”¥ Client: Sending patient queue subscription for:', patientId);
          wsRef.current?.send(JSON.stringify({ type: 'subscribe_patient_queue', patientId }));
        } else {
          console.log('ðŸ”¥ Client: No subscription sent - not admin and no patientId');
        }
      };

      wsRef.current.onmessage = (event) => {
        try {
          const update: QueueUpdate = JSON.parse(event.data);
          console.log('ðŸ”¥ Queue update received:', update);

          if (update.type === 'queue_position') {
            setQueuePosition(update.data);
          } else if (update.type === 'admin_queue_update') {
            setQueueTokens(update.data);
          } else if (update.type === 'full_queue_update') {
            setQueueTokens(update.data);
          }
        } catch (error) {
          console.error('Failed to parse queue update:', error);
        }
      };

      wsRef.current.onclose = () => {
        console.log('ðŸ”¥ Queue WebSocket disconnected');
        setIsConnected(false);

        // Attempt to reconnect after 3 seconds
        reconnectTimeoutRef.current = setTimeout(() => {
          console.log('ðŸ”¥ Attempting to reconnect queue WebSocket...');
          connect();
        }, 3000);
      };

      wsRef.current.onerror = (error) => {
        console.error('ðŸ”¥ Queue WebSocket error:', error);
        setIsConnected(false);
      };
    } catch (error) {
      console.error('Failed to create WebSocket connection:', error);
      setIsConnected(false);
    }
  };

  useEffect(() => {
    connect();

    return () => {
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, [patientId, isAdmin]);

  const refreshQueue = () => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      if (isAdmin) {
        wsRef.current.send(JSON.stringify({ type: 'subscribe_admin_queue' }));
      } else if (patientId) {
        wsRef.current.send(JSON.stringify({ type: 'subscribe_patient_queue', patientId }));
      }
    }
  };

  return {
    queuePosition,
    queueTokens,
    isConnected,
    refreshQueue
  };
}

client/src/pages/clinic-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { Plus, Building2, Users, Calendar, Pill, Edit2, Trash2 } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

interface Clinic {
  id: string;
  name: string;
  address: string;
  phone: string;
  email: string | null;
  description: string | null;
  operatingHours: any;
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
}

interface ClinicStats {
  totalUsers: number;
  totalAppointments: number;
  totalMedicines: number;
  activeStaff: number;
}

export default function ClinicManagement() {
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [editingClinic, setEditingClinic] = useState<Clinic | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    phone: "",
    email: "",
    description: "",
    operatingHours: {
      monday: { open: "09:00", close: "17:00", closed: false },
      tuesday: { open: "09:00", close: "17:00", closed: false },
      wednesday: { open: "09:00", close: "17:00", closed: false },
      thursday: { open: "09:00", close: "17:00", closed: false },
      friday: { open: "09:00", close: "17:00", closed: false },
      saturday: { open: "09:00", close: "13:00", closed: false },
      sunday: { open: "09:00", close: "13:00", closed: true }
    }
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: clinics, isLoading } = useQuery({
    queryKey: ["/api/clinics"],
  });

  const createClinicMutation = useMutation({
    mutationFn: (data: typeof formData) => apiRequest("/api/clinics", {
      method: "POST",
      body: JSON.stringify(data),
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clinics"] });
      setIsCreateDialogOpen(false);
      setFormData({
        name: "",
        address: "",
        phone: "",
        email: "",
        description: "",
        operatingHours: {
          monday: { open: "09:00", close: "17:00", closed: false },
          tuesday: { open: "09:00", close: "17:00", closed: false },
          wednesday: { open: "09:00", close: "17:00", closed: false },
          thursday: { open: "09:00", close: "17:00", closed: false },
          friday: { open: "09:00", close: "17:00", closed: false },
          saturday: { open: "09:00", close: "13:00", closed: false },
          sunday: { open: "09:00", close: "13:00", closed: true }
        }
      });
      toast({
        title: "Clinic Created",
        description: "New clinic has been successfully registered.",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to create clinic. Please try again.",
        variant: "destructive",
      });
    },
  });

  const updateClinicMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<typeof formData> }) => 
      apiRequest(`/api/clinics/${id}`, {
        method: "PUT",
        body: JSON.stringify(data),
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clinics"] });
      setIsEditDialogOpen(false);
      setEditingClinic(null);
      toast({
        title: "Clinic Updated",
        description: "Clinic information has been successfully updated.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update clinic. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteClinicMutation = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/clinics/${id}`, {
      method: "DELETE",
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/clinics"] });
      toast({
        title: "Clinic Deleted",
        description: "Clinic has been successfully removed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete clinic. Please ensure no users are associated with it.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingClinic) {
      updateClinicMutation.mutate({ id: editingClinic.id, data: formData });
    } else {
      createClinicMutation.mutate(formData);
    }
  };

  const openEditDialog = (clinic: Clinic) => {
    setEditingClinic(clinic);
    setFormData({
      name: clinic.name,
      address: clinic.address,
      phone: clinic.phone,
      email: clinic.email || "",
      description: clinic.description || "",
      operatingHours: clinic.operatingHours || {
        monday: { open: "09:00", close: "17:00", closed: false },
        tuesday: { open: "09:00", close: "17:00", closed: false },
        wednesday: { open: "09:00", close: "17:00", closed: false },
        thursday: { open: "09:00", close: "17:00", closed: false },
        friday: { open: "09:00", close: "17:00", closed: false },
        saturday: { open: "09:00", close: "13:00", closed: false },
        sunday: { open: "09:00", close: "13:00", closed: true }
      }
    });
    setIsEditDialogOpen(true);
  };

  const ClinicCard = ({ clinic }: { clinic: Clinic }) => {
    const { data: stats } = useQuery<ClinicStats>({
      queryKey: [`/api/clinics/${clinic.id}/stats`],
    });

    return (
      <Card data-testid={`clinic-card-${clinic.id}`}>
        <CardHeader>
          <div className="flex items-start justify-between">
            <div>
              <CardTitle className="flex items-center gap-2">
                <Building2 className="h-5 w-5" />
                {clinic.name}
                {clinic.isActive ? (
                  <Badge variant="default">Active</Badge>
                ) : (
                  <Badge variant="secondary">Inactive</Badge>
                )}
              </CardTitle>
              <CardDescription>{clinic.address}</CardDescription>
            </div>
            <div className="flex gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => openEditDialog(clinic)}
                data-testid={`edit-clinic-${clinic.id}`}
              >
                <Edit2 className="h-4 w-4" />
              </Button>
              <AlertDialog>
                <AlertDialogTrigger asChild>
                  <Button
                    variant="outline"
                    size="sm"
                    data-testid={`delete-clinic-${clinic.id}`}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </AlertDialogTrigger>
                <AlertDialogContent>
                  <AlertDialogHeader>
                    <AlertDialogTitle>Are you sure?</AlertDialogTitle>
                    <AlertDialogDescription>
                      This action cannot be undone. This will permanently delete the clinic
                      and remove it from our servers. Make sure no users are associated with this clinic.
                    </AlertDialogDescription>
                  </AlertDialogHeader>
                  <AlertDialogFooter>
                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                    <AlertDialogAction
                      onClick={() => deleteClinicMutation.mutate(clinic.id)}
                    >
                      Delete
                    </AlertDialogAction>
                  </AlertDialogFooter>
                </AlertDialogContent>
              </AlertDialog>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-2 text-sm text-muted-foreground">
            <p><strong>Phone:</strong> {clinic.phone}</p>
            {clinic.email && <p><strong>Email:</strong> {clinic.email}</p>}
            {clinic.description && <p><strong>Description:</strong> {clinic.description}</p>}
          </div>

          {stats && (
            <div className="mt-4 grid grid-cols-2 gap-4">
              <div className="flex items-center gap-2">
                <Users className="h-4 w-4 text-blue-500" />
                <span className="text-sm">{stats.totalUsers} Users</span>
              </div>
              <div className="flex items-center gap-2">
                <Calendar className="h-4 w-4 text-green-500" />
                <span className="text-sm">{stats.totalAppointments} Appointments</span>
              </div>
              <div className="flex items-center gap-2">
                <Pill className="h-4 w-4 text-purple-500" />
                <span className="text-sm">{stats.totalMedicines} Medicines</span>
              </div>
              <div className="flex items-center gap-2">
                <Users className="h-4 w-4 text-orange-500" />
                <span className="text-sm">{stats.activeStaff} Active Staff</span>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    );
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader>
                <div className="h-6 bg-gray-200 rounded w-3/4"></div>
                <div className="h-4 bg-gray-200 rounded w-1/2"></div>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  <div className="h-4 bg-gray-200 rounded"></div>
                  <div className="h-4 bg-gray-200 rounded w-5/6"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Clinic Management</h1>
          <p className="text-muted-foreground">Register and manage multiple clinic locations</p>
        </div>

        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="create-clinic-button">
              <Plus className="h-4 w-4 mr-2" />
              Register New Clinic
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Register New Clinic</DialogTitle>
              <DialogDescription>
                Add a new clinic location to your healthcare network.
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Clinic Name *</Label>
                  <Input
                    id="name"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                    data-testid="input-clinic-name"
                  />
                </div>
                <div>
                  <Label htmlFor="phone">Phone Number *</Label>
                  <Input
                    id="phone"
                    value={formData.phone}
                    onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                    required
                    data-testid="input-clinic-phone"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="address">Address *</Label>
                <Input
                  id="address"
                  value={formData.address}
                  onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                  required
                  data-testid="input-clinic-address"
                />
              </div>

              <div>
                <Label htmlFor="email">Email (Optional)</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  data-testid="input-clinic-email"
                />
              </div>

              <div>
                <Label htmlFor="description">Description (Optional)</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  rows={3}
                  data-testid="input-clinic-description"
                />
              </div>

              <div className="flex justify-end gap-2">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsCreateDialogOpen(false)}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createClinicMutation.isPending}
                  data-testid="submit-clinic-form"
                >
                  {createClinicMutation.isPending ? "Creating..." : "Register Clinic"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Edit Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit Clinic</DialogTitle>
            <DialogDescription>
              Update clinic information and settings.
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="edit-name">Clinic Name *</Label>
                <Input
                  id="edit-name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  required
                />
              </div>
              <div>
                <Label htmlFor="edit-phone">Phone Number *</Label>
                <Input
                  id="edit-phone"
                  value={formData.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                  required
                />
              </div>
            </div>

            <div>
              <Label htmlFor="edit-address">Address *</Label>
              <Input
                id="edit-address"
                value={formData.address}
                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                required
              />
            </div>

            <div>
              <Label htmlFor="edit-email">Email (Optional)</Label>
              <Input
                id="edit-email"
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              />
            </div>

            <div>
              <Label htmlFor="edit-description">Description (Optional)</Label>
              <Textarea
                id="edit-description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                rows={3}
              />
            </div>

            <div className="flex justify-end gap-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsEditDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={updateClinicMutation.isPending}
              >
                {updateClinicMutation.isPending ? "Updating..." : "Update Clinic"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {/* Clinics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {clinics?.map((clinic: Clinic) => (
          <ClinicCard key={clinic.id} clinic={clinic} />
        ))}
      </div>

      {(!clinics || clinics.length === 0) && (
        <Card>
          <CardContent className="text-center py-8">
            <Building2 className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">No Clinics Registered</h3>
            <p className="text-muted-foreground mb-4">
              Get started by registering your first clinic location.
            </p>
            <Button onClick={() => setIsCreateDialogOpen(true)}>
              <Plus className="h-4 w-4 mr-2" />
              Register First Clinic
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

